css:
@-moz-document domain(sevdesk.de) {

/*Oberste Menüzeile in schwarz*/
.logo_container, #main-header .custom-header-dropdown, #main-header {background-color: black!important;}
#main-header .custom-header-dropdown {border-color: #555 !important;}
#main-header .header-search__input {color:white!important; letter-spacing:0.04em;}
#main-header .custom-header-dropdown {color: rgba(255,255,255,0.5) !important;}
.header-search__input {border: 1px solid #555;}
.header-links li.quick-link a {letter-spacing: 0.03em;}
.header button i, i.ti-time, i.ti-user, .header-links li.quick-link:not(.open) a {color: #ddd; letter-spacing: 0.03em;}


.header-links li.quick-link a:active, .header-links li.quick-link a:focus, .header-links li.quick-link a:hover, .header-links li.quick-link div:active, .header-links li.quick-link div:focus, .header-links li.quick-link div:hover {color: white;}

/*Linke Seitenleiste in dunkelgrau*/
.sidebar-navigation {background-color: #393d44; min-width: 182px;}

.sidebar-navigation.small-navi {min-width: 65px; width:65px}

.main-nav > li > a {color: #ddd; letter-spacing: 0.03em;}

/* Buttons dunkler */
.btn-primary {letter-spacing: 0.04em; font-weight:400; background:-moz-linear-gradient(top,#2086e0,#1b42bc);}


/* Allgemeiner Hintergrund dunkler */
.page-content, .content-header {background-color: #bbb;}
.dash-corona-vat-info > div {border: 1px solid black;}


/* Titelleiste an allgemeinen Hintergrund anpassen */
.page-header {padding-top:18px; box-shadow:none!important;}
.h1 .small, .h1 small, .h2 .small, .h2 small, .h3 .small, .h3 small, .h4 .small, .h4 small, .h5 .small, .h5 small, .h6 .small, .h6 small, h1 .small, h1 small, h2 .small, h2 small, h3 .small, h3 small, h4 .small, h4 small, h5 .small, h5 small, h6 .small, h6 small {letter-spacing:0.04em; color:black; font-weight:600;}
.navigation-wrapper, .navbar, .page-header {border-bottom:0px!important;}


/* Kleine Bildschirmbreiten FIXES */
@media screen and (max-width: 1068px) {
ul.content-header--controls {width: 250px; transform: scale(0.9); padding-left:20px}
button.dropdown-toggle {width:40px}
span.button-line button.btn.btn-default {width: 16px;}
div.dropdown button.btn.btn-default span:nth-child(2), span.button-line span div.dropdown button.btn.btn-default span.caret  {display:none;}
span.button-line button.btn.btn-default {font-size:0px;}
span.ti-export, span.ti-filter {font-size:13px;}
span.button-line {width: 88px;}
}


/* Tabellendesign (abwechselnde Zeilenfarben) + angedeutete Spalten */
.ui-grid-styled-table table tr td:nth-child(even), .ui-grid-styled-table.pay-index-content table tr:nth-child(4n+4) td:nth-child(even), .ui-grid-canvas .ui-grid-row .ui-grid-cell:nth-child(even) {border-left:1px solid #eee; border-right:1px solid #eee;}
.ui-grid-styled-table table tr:nth-child(odd):not(:first-child), .ui-grid-styled-table.pay-index-content table tr:nth-child(4n+4), .ui-grid-canvas .ui-grid-row:nth-child(even) {background-color: #f6f6f6;}
.ui-grid-cell {padding: 0 8px;}
.default-grid .ui-grid-row:hover {background-color: rgba(0,0,0,0.1);}


/* Änderung für den Hilfe-Button */
.help-menu-btn {height: 50px; width: 50px; right: 0px; bottom: 0px; z-index: 100; position: fixed; border-radius: 0px;}
.help-icon {height: 27.5px; position: absolute; right: 12px; top: 12px;}

}
