css:
@-moz-document domain("ps4.in.ua") {
.ipsApp{
    background-color: black;
}
.ipsPad_half, .ipsPad_half a, .ipsPad_half a strong, .ipsWidget_inner, .ipsBox, #ipsLayout_header nav::after, .ipsNavBar_secondary a {
	background: #14161b;
    color: white!important;
}
.sp-button{
	border-color: #eee;
}
.sp-selected-button{
	background: white;
}
.ipsDataList{
    background: #14161b;
}
.elProfileTabs_content, #elProfileTab_activity{
	background: #14161b;
    color: white;
}
.ipsDataItem_stats_number, .chatbox_msg, .ipsType_richText{
 	color:#eee!important;
}
.ipsBreadcrumb ul, .ipsType_pageTitle, .cke_widget_editable p{
	color: white;
}
.cke_wysiwyg_div, [data-ipseditor],.ipsComposeArea_dummy {
    background-color: #444!important;
    color: white!important;
}
.ipsPageHeader, .ipsPageHeader h3, .ipsPageHeader p, .ipsStreamItem, .ipsStreamItem a, .ipsDataItem_main a, .ipsType_sectionHead, .ipsDataItem {
	background: #14161b!important;
    color: white!important;
    border-color: #333!important;
}
#elSearch, #elSearch input::placeholder{
	background: #666!important;
    color: white!important;
}
.elUserLink_menu, .ipsMenu_item, #elFullNotifications_menu {
	background: black;
    border: 1px solid #444;
}
.ipsMenu_title, .ipsMenu_item a, .ipsSideMenu_subTitle, .ipsWidget_inner a, #elFooterLinks a{
	color: white!important;
}
a[data-role="sortButton"], .ipsPagination_page a, .ipsPagination_next a, .ipsPagination_pageJump a, .ipsPagination_last a, .ipsButtonBar a, .ipsMenu_title{
	background: #444!important;
}
.ipsTags .ipsTag{
	background: black!important;
    color: white!important;
}
.cPost.ipsComment .ipsType_richText *{
	background: none!important;
    color: white!important;
}
.ipsQuote{
	background: #444;
}
.ipsQuote_contents a{
	color: black!important;
}
#elFullInbox_menu{
	background: black;
}
.ipsMenu_footerBar a{
	color: white;
}
.ipsComment a {
    font-style: italic;
    color: white;
}
.ipsAreaBackground_light, .cMessage_active{
	background: black!important;
}
.cAuthorPane_info li > span:first-child{
	color: white!important;
}
.cPost_contentWrap a[data-mentionid]{
	color: #444;
}
.ipsTruncate_more{
 	color: white!important;   
}
.ipsPager{
 	color: white;   
}
.cAuthorPane_author a{
	font-style: normal!important;
    font-size: 16px;
}

.ipsFieldRow_content input[type="text"]{
	color:black;
}
/* Links */
.username,
.ipsDataItem_title a, 
.ipsDataItem_subList a, 
.ipsDataItem_lastPoster__title a,
.chatterName,
.ipsWidget .ipsDataItem_main .ipsTruncate_line{
	color: white;
}
.ipsType_break, .ipsJS_show.ipsType_normal{
	color: #ccc;
}
.ipsType_break:hover{
	color: #eee;
}
.cPost {
	background: #14161b;
    color: white;
}
.cPost a{
	color: white;
    font-style:italic;
}
.ipsBreadcrumb .ipsType_light {
	color: white!important;
    font-size: 14px;
    font-weight: bold;
}
.ipsType_light:hover{
	color: #eee!important;
}
/* Chat */
.ipsField_fullWidth.chatInput{
	background: #111;
    color: white;
}
#chatboxRoom{
	background: #333;
}
}
