css:
@-moz-document domain("angular.io") {
.nav-link.home img, .app-toolbar .toolbar-external-icons-container svg {
    opacity: 0.3;
}
.app-toolbar a {
    color: #aaa;
}
aio-search-box.search-container input {
    background-color: #222;
    color: #ccc;
}
aio-search-results .search-results p, aio-search-results .search-results li, aio-search-results .search-results h3, aio-search-results .search-results ul, aio-search-results .search-results {
    background-color: #222;
    color: #0f0;
}

.toc-inner ul.toc-list:not(.embedded) li:before {
	border-color: #333;
}

table tbody>tr>td, table tbody>tr>th {
    border-color: #333;
}
table tbody>tr>th {
    background-color: #222;
}

.mat-toolbar.mat-primary {
    background-color: #151515;
    color: #f0f;
}
html, body, .content, .mat-drawer-container, .mat-drawer-container, input, li, ol, p, ul, code, h1, h2, h3, h4, h5, h6, table {
    background-color: #151515;
    color: #ccc;
}
code {
    color: #fff;
}
.sidenav-content a {
    color: #39f;
}
.vertical-menu-item:hover {
    background-color: #101010;
    color: #1976d2;
    text-shadow: none;
}
aio-resource-list .subcategory-title, table thead>tr>th {
    background-color: #222;
    color: #ccc;
}
table code {
    color: #fff;
}
mat-sidenav-container.sidenav-container.mat-drawer-container.mat-sidenav-container, mat-sidenav-container .sidenav-content {
    background-color: #151515;
    color: #f90;
}
mat-sidenav.mat-sidenav.sidenav, .form-select-button {
    background-color: #151515;
    color: #999;
    border: 1px solid #333;
}
mat-sidenav .doc-version {
    border-top: 1px solid #333;
}
hr {
    border: none;
    background: #333;
    height: 1px;
}
.content .lightbox {
    background-color: #151515;
    color: #0f0;
    border: 1px solid #333;
}
.lightbox img {
    filter: invert(1);
}
.mat-tab-label {
    color: #1e88e5;
}
.mat-tab-body-content, .mat-tab-body-content code {
    background-color: #111;
}
code-example, code-example:not(.no-box) {
    background-color: #111;
    border: 1px solid #333;
}
code-tabs mat-tab-header, code-example header {
    background-color: #191919;
    color: #1e88e5;
}


    code-example code {
        background-color: #111;
        color: #ccc;
    }
    .pln, code-example code .pln {
        color: #eee;
    }
    .tag, code-example code .tag {
        color: #c66;
    }
    .kwd, code-example code .kwd {
        color: #a3b;
    }
    .clo, .clocode-example code .clo,
	.opn, code-example code .opn,
	.pun, code-example code .pun {
        color: #aa0;
    }
    .typ, code-example code .typ {
        color: #d33;
    }
    .str, code-example code .str,
    .atv, code-example code .atv {
        color: #6b0;
    }
    .atn, code-example code .atn {
        color: #f90;
    }
    .lit, code-example code .lit {
        color: #09f;
    }
    .cli-name .kwd {
        color: #fff;
    }

.toc-inner ul.toc-list li.h1:after {
    content:"";
    display: block;
    height: 1px;
    width: 40%;
    margin: 7px 0 4px;
    background: #333;
    clear: both;
}
footer {
    background-color: #151515;
}
footer h3, footer a, footer aio-footer>* {
    color: #666;
}
footer:after {
    background-image: none;
}
.card-container .docs-card, .marketing-banner {
    background-color: #151515;
}
.blm-message {
    display: none;
}
.alert.is-helpful *, .alert.is-important * {
    background: transparent;
}

.card-container a.docs-card {
    color: #ccc;
    background-color: #151515;
    border: 1px solid #333;
}

.card-container a.docs-card  section,
.card-container a.docs-card  p {
    color: #ccc;
    background-color: #151515;
}

.card-container a.docs-card .card-footer {
    border-top: 1px solid #333;
    color: #ccc;
}

.card-container a.docs-card:hover p,
.card-container a.docs-card:hover {
    color: #fff;
}

.card-container a.docs-card:hover section {
    color: #1976d2;
}

.docs-content .api-list li a:hover {
    background-color: #151515;
}
.docs-content .api-list li a {
    transition: all .1s;
}

aio-api-list .form-search input {
    color: #ccc;
}
ul.form-select-dropdown, .form-select-dropdown li {
    background-color: #111;
}
.form-select-dropdown li.selected {
    background-color: #333;
}
.form-select-dropdown li:hover {
    background-color: #222;
}
}
