css:
@-moz-document regexp("https://ionicframework.com/(.*)") {
internal-ad {
    margin: 0;
    display: none;
 }

internal-ad * {
    display: none;
}

html, body {
    background-color: #151515;
    color: #ccc;
}


docs-nav, docs-menu, docs-menu header, docs-header header, stencil-route-switch {
    background-color: #151515;
}

docs-header header a svg.HeaderLogo,
docs-menu header a svg.MenuLogo
{
    color: rgb(115, 132, 154);
}

docs-root docs-menu > header {
    border-color: #333;
}

docs-root docs-menu {
    border-right: 1px solid #333;
}

docs-header header a.link-active {
    color: #ccc;
}

docs-page main article p img,
.Dropdown-arrow, 
docs-header header a.link-active svg {
	filter: invert(1);
}
docs-header header .SectionNav docs-dropdown.Dropdown button.Dropdown-button {
    border: 1px solid #333;
}
docs-header header .SectionNav docs-dropdown.Dropdown.is-open button.Dropdown-button {
    border: 1px solid #666;
}
docs-header header .Dropdown-panel>section:not(:last-child) {
    border-bottom: 1px solid #333;
}
docs-header header .Dropdown-button-label {
    color: #ccc;
}

docs-header header .Dropdown-panel {
    background-color: #151515;
    color: rgb(115, 132, 154);
    border: 1px solid #666;
}

nav.UtilNav ionic-search .SearchBtn__lg {
    border: 1px solid #666;
}

nav.UtilNav ionic-search .SearchBtn__lg__key {
    background-color: #151515;
    color: rgb(115, 132, 154);
    border: 1px solid #666;
}

docs-header header .search-box {
	background-color: #151515;
    border: 1px solid #333;
}

docs-header header .search-box input,
docs-header header .search-box .SearchResults strong,
docs-header header .search-box .Search__Defaults__Section ul a strong {
	color: #fff;
}

docs-header header .search-box .Search__Defaults {
    border-top: 1px solid #333;
}

docs-header header .search-box svg.search-static {
    filter: invert(1);
}

docs-header header .search-box .SearchMore__link {
    border-top: 1px solid #666;
	background-color: #222;
    color: #ccc;
}

docs-header header .search-box a:not(.link-active):hover {
    color: #303943;
    background-color: #222;
}

docs-header header .search-box .SearchMore__link:hover {
    color: #ccc;
    background: inherit;
}

docs-header header .search-box .SearchMore__link svg {
    filter: invert(1);
}


@media (hover: hover) {
	docs-header header .Dropdown-panel a:not(.link-active):hover {
    	color: #fff;
	}
}


docs-menu {
	border-right: none;
    border-image-width: 0;
}

docs-menu docs-nav a.Nav-link:not(.Nav-link--active):hover {
    color: #fff;
}

docs-menu docs-nav a.Nav-link--active.Nav-link {
    background-color: #000;
    color: #3880ff;
}

stencil-route-switch section.MenuControls framework-select button.Dropdown-button {
    border: 1px solid #333;
}

stencil-route-switch section.MenuControls {
    border: none;
}

docs-nav header.Nav-header {
    color: #ccc;
}

body, html, docs-root, stencil-router, docs-page h1, docs-page h2, docs-page h3, docs-page h4, docs-page h5, docs-page h6, table * {
    background-color: #151515;
    color: #ccc;
}
table tbody, table tbody tr, table tbody th, table tbody td, table thead th {
    border: 1px solid #333;
}

docs-page code {
    background-color: #111;
    color: #fff;
}

docs-code pre {
    background-color: #111;
}

docs-code code .token.comment {
    
}
docs-code code .token.selector {
    color: #c66;
}

docs-code code .token.punctuation {
    color: #cc0;
}


docs-page blockquote {
    color: rgb(150, 111, 69);
    border-left: 4px solid rgba(255, 204, 95, 0.5);
    background: rgba(255, 250, 237, 0.10);
}
docs-page blockquote code {
    background: rgba(0, 0, 0, 0.33);
    color: rgb(150, 111, 69);
}

docs-page docs-card .Card-container {
    background-color: #111;
    color: #ccc;
}

docs-page docs-card .Card {
    background-color: #111;
    color: #ccc;
    border: 1px solid #333;
}


docs-page docs-card .Card-container header {
    color: #ccc;
}

docs-page .code-text__copy-link {
    background-color: #3880ff33;
    color: #3880ff;
}

.docs-demo-mode-toggle {
    background-color: #000;
    color: #aaa;
}

.docs-demo-mode-toggle button.is-selected {
    background-color: #151515;
    color: #ccc;
}

docs-page-footer contributor-list img {
    border: 2px solid #333;
}

}
