.sc-gas-price-denominator,
.sc-gas-price-divider,
.sc-gas-price-fraction,
.sc-gas-price-numerator,
.sc-gas-price.sc-club-gas-prices-price,
.sc-pc-map-price-label-small,
.sc-pc-single-price .sc-price
 {
	color: #07ff24!important;
	text-shadow: 1px 1px #000!important;
