@-moz-document domain("samsclub.com") {


/* Cart _______________________________________________________________*/

.sc-mini-cart-count {
    left: 4px;
    top: -5px;
}

.sc-desktop .sc-mini-cart-icon {
	background: url(https://i.ibb.co/42GPC9s/shoppingcart.png) no-repeat!important;
	box-sizing: border-box;
}

.sc-desktop .sc-mini-cart-icon {
    background-size: 27px!important;
    height: 30px!important;
    padding-left: 31px!important;
    width: 10px!important;
}

/* Arrows _______________________________________________________________*/

.sc-chevron-decorator-button-right {
    transform: none;
}

/* Left _________________*/

.sc-chevron-decorator-button-left {
	background: url(https://i.ibb.co/2cy7Nyv/dark-arrow-left.png) no-repeat!important;
	box-sizing: border-box;
}

/* Right _________________*/

.sc-chevron-decorator-button-right {
	background: url(https://i.ibb.co/xHgfd0L/dark-arrow-right.png) no-repeat!important;
	box-sizing: border-box;
}

.sc-chevron-decorator-button-left, .sc-chevron-decorator-button-right {
    background-size: 18px!important;
    height: 26px!important;
    padding-left: 17px!important;
    width: 10px!important;
}

/* Checkmark _______________________________________________________________*/

.sc-checkbox-field input:checked+.option-content:after {
    content: "";
}

.sc-checkbox-field input:checked+.option-content:before {
    background-color: #0000;
}

/* Checked ________________________*/

.sc-checkbox-field input:checked+.option-content:before
{
	background: url(https://i.ibb.co/HgsTqFS/checkbox-checked.png) no-repeat!important;
	box-sizing: border-box;
}

.sc-checkbox-field .option-content:before {
    background-color: #0000;
}

.sc-checkbox-field .option-content:before {
    border: none;
    margin: 0;
    top: -10px;
}

/* Unchecked ________________________*/

.sc-checkbox-field .option-content:before
{
	background: url(https://i.ibb.co/fM9ptv1/checkbox-empty.png) no-repeat!important;
	box-sizing: border-box;
}

.sc-checkbox-field .option-content:before, .sc-checkbox-field input:checked+.option-content:before {
    background-size: 27px!important;
    height: 28px!important;
    padding-left: 22px!important;
