css:
@-moz-document url-prefix("https://www.blogdepelis.tv/") {
body, .sidebar.c-4-12, #header #s {background-color: #000;}
body {color: #eee;}
#site-header {background: #101010;box-shadow: 0 1px 2px 0 #888;}
#logo img {background-color: #ccc;}
#header #s {border-color: #444;}
#s, .woocommerce-product-search .search-field {color: #eee;}
.navigation ul li:hover {background: #150150;}
.latestPost {background: #151515;box-shadow: 0 1px 2px 0 #444;}
.thecontent a, .post .tags a, #commentform a, .author-posts a:hover, .fn a, a {color: #aaa;}
.latestPost .title, .latestPost .title a {color: #ccc;}
.pagination {background: #151515;box-shadow: 0 1px 2px 0 #444;}
footer {background: #101010;box-shadow: 0 1px 2px 0 rgba(0,0,0,.05);}
#copyright-note {color: #aaa;}
.copyrights a {color: #fff;}
div#page.single, #page.error, .woocommerce #page {background: #101010;box-shadow: 0 1px 2px 0 #444;}
.single-title {color: #FFF;}
.separator span h2 {color: #eee;}
h4 {color: #ddd;}
.related-posts article header {border: 1px solid #444;}
#reactions {color: #ddd!important;}
.sidebar .widget {background: #202020;box-shadow: 0 1px 2px 0 #444;}
.widget .widget-title {color: #eee;}
.widget > h3 {border-bottom: 1px solid #444;}
.sidebar.c-4-12 .advanced-recent-posts li, .sidebar.c-4-12 .popular-posts li, .sidebar.c-4-12 .category-posts li, .sidebar.c-4-12 .related-posts-widget li, .sidebar.c-4-12 .author-posts-widget li {background: #202020;}
.advanced-recent-posts li, .popular-posts li, .category-posts li, .related-posts-widget li, .author-posts-widget li {color: #ccc;}
.sidebar.c-4-12 a {color: #ccc;}

}
