css:
@-moz-document regexp("https://picrew.me/image_maker/.*") {
.imagemaker_ad_wrapper {
	display: none !important;
}

.imagemaker_container_wrapper,
.randommaker_container_wrapper {
	width: 100% !important;
}

.imagemaker_container,
.randommaker_container {
	height: 100% !important;
}

@media screen and (min-width: 800px) {
	.imagemaker_container {
		flex-direction: row;
	}

	.imagemaker_canvas_wrapper {
		height: 100% !important;
		max-height: unset;
		width: 100%;
		max-width: 50vw;
	}
	
	.imagemaker_info_wrapper {
		width: 600px;
		height: 800px;
		max-height: 90vh;
		top: 50%;
		left: 50%;
		transform: translatex(-50%) translatey(-50%);
	}
}

.imagemaker_menu_btn {
	width: 120px;
}

.imagemaker_parts_menu_wrapper {
	padding-left: 120px;
}

.imagemaker_parts_menu ul li {
	width: 56px;
}

.imagemaker_itemlist ul li {
	width: 24%;
	max-width: 160px;
}

.imagemaker_info_show_btn {
	top: auto;
	bottom: 4px;
}

}
