css:
@-moz-document domain("notion.so") {
/* 
在notion笔记的月历视图中，以大图预览子页面的logo
     height:calc(100%-44px) !important;
 */
.notion-calendar-view div[class="notion-selectable notion-page-block notion-collection-item"] {
    height: 88px !important;
}

.notion-calendar-view div[class="notion-record-icon notranslate"],
.notion-calendar-view div[class="notion-record-icon notranslate"] * {
    height: 80px !important;
    width: auto !important;
    padding: 0 0 0 0 !important;
}

.notion-calendar-view div[class="notion-selectable notion-page-block notion-collection-item"] div[role="button"],
.notion-calendar-view div[class="notion-selectable notion-page-block notion-collection-item"] > a > div > div > div {
    height: 80px !important;
    padding: 0 0 0 0 !important;
}
}
