css:
@-moz-document domain("diki.pl") {
/* 
Author: Vesterlay 
Version: 1.3
Licence: MIT UNLESS STATED OTHERWISE
*/
:root {
    --result-border: #8c8c8c30;
    --background-color: #222;
}

p,
ul,
ol,
td {
    color: #ccc;
}

li.ui-menu-item {
    color: #000;
}
.dikibody h1.mainPageSloganUnderLogo {
    color: #C4B5B5;
}
#thinHeaderMenu .menuItem.menuProfileItem {
    background: #c30407!important;
}
#thinHeaderMenuIcon div,
#thinHeaderMenuIcon div::before,
#thinHeaderMenuIcon div::after {
    background-color: #fff;
}
div.dikibody div.partOfSpeechSectionHeader span.partOfSpeech,
span.headerPartOfSpeech {
    color: #C20A0A;
}
div.collocationHeadwordGroupHeader:not(.noHover):hover,
div.browsedCollocationHeader:not(.noHover):hover,
div.browsedCollocationHeader.currentCollocation,
div.collocationListHeader {
    background: #353d4075;
}
div.collocationListHeader {
    background: unset!important;
}
div.dikibody h2.dictionarySectionHeader,
span.hw,
div.fentry,
div.similarCollocations,
span.headerPartOfSpeech,
div.dikibody .picturesource,
div.dikibody .picturedescription,
div.dikibody div.seealso,
.showCollocationsLink,
div.pf,
.partOfSpeechSectionHeader,
div.relatedCollocationGroup,
.automaticCollocationTranslation,
.dictionaryEntryHeaderAdditionalInformation,
p.sentencesSourceInfo,
div.collocationHeadwordGroupHeader a.collocationBrowseLink,
div.collocationListHeader a.collocationBrowseLink,
div.browsedCollocationHeader a.collocationBrowseLink {
    color: #fff!important;
}
p.sentencesSourceInfo:hover,
.gray,
.gray:visited,
.gray:not(.noHover):hover {
    color: #fff!important;
}
span.shl {
    background-color: #393636!important;
}
#thinHeaderMenuIcon:not(.noHover):hover div,
#thinHeaderMenuIcon:not(.noHover):hover div::before,
#thinHeaderMenuIcon:not(.noHover):hover div::after {
    background-color: #a2a2a2;
}
body.dikibody,
.qTipPopupModal.qtip.qtipOverEntireViewport,
.qTipPopupModal.qtip .qtip-content {
    background-color: var(--background-color);
}
div.dikibody .foreignToNativeMeanings div.exampleSentence,
div.dikibody .foreignToNativeMeanings div.ref,
div.dikibody .nativeToForeignEntrySlices div.exampleSentence,
div.dikibody .nativeToForeignEntrySlices div.ref,
span.fentrymain {
    color: #ccc;
}
div.vf,
.qtip-default {
    color: #ccc!important;
}
div.dikibody ul.wordByWordTranslation li div.word {
    background-color: #732628;
}
div.dikibody .foreignToNativeMeanings li.hl,
div.dikibody .nativeToForeignEntrySlices li.hl {
    background-color: #c3040724;
}
div.dikibody div.diki-results-container {
    background-color: #222;
    border: 1px solid var(--result-border);
}
div.dikibody div.diki-results-container div.diki-results-left-column {
    border-right: 1px solid var(--result-border);
}
div.dikibody div.diki-results-container div.diki-results-right-column {
    border-left: 1px solid var(--result-border);
}
body.isDesktop.clearSearchForIosEnabled.dikibody div.dikibody div#contentWrapper div.helpTroubleshootingContents.dikicolumn p,
body.isDesktop.clearSearchForIosEnabled.dikibody div.dikibody div#contentWrapper div.helpTroubleshootingContents.dikicolumn ul li,
body.isDesktop.clearSearchForIosEnabled.dikibody div.dikibody div#contentWrapper div.lastSearchesContents.dikiPageContents div.cityBgr div.lastSearchesRow p,
body.isDesktop.clearSearchForIosEnabled.dikibody div.dikibody div#contentWrapper div.lastSearchesContents.dikiPageContents div.lastSearchesRow.center p,
div.dikicolumn p {
    color: #fff;
}
}
