css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@namespace html url(http://www.w3.org/1999/xhtml);
@-moz-document url("chrome://mozapps/content/extensions/extensions.xul") {



#extensionsManager {
        background: RGBa(255,255,255,0.2) !important;
        -moz-appearance: -moz-win-glass !important;
        opacity: 0.99 !important;}

#extensionsBox{
        -moz-appearance: -moz-win-glass !important;
        background: RGBa(255,255,255,0.0) !important;
        margin: 10px 0px 0px !important;
        padding: 0px 0px 0px !important;
        opacity: 0.99 !important;}

#styles-container{
        -moz-appearance: -moz-win-glass !important;
        background: RGBa(255,255,255,0.0) !important;
        margin: 0px 0px 0px !important;
        padding: 0px 0px 0px !important;
        opacity: 0.99 !important;}

/* Vista Aero Black page selector */

#topStackBar .viewSelector.chromeclass-toolbar {
	-moz-appearance: none !important;
        border: none !important;
        background: #0f0f14 url("data:image/png;base64,") repeat-x center !important;}

#topStackBar radio {
	-moz-appearance: none !important;
        border: none !important;
        color: White !important;
        background: transparent !important;}
#topStackBar radio:not([selected="true"]) {
	-moz-appearance: none !important;
        border: none !important;
        color: #777 !important;
        background: transparent !important;}
#topStackBar radio:hover {
	-moz-appearance: none !important;
        border: none !important;
        color: #ccc !important;
        background: transparent !important;}

.viewButtonLabel {
        opacity: 1.00 !important;}

/* Glass Notifications */

#addonsMsg notification {
        font-style: italic;
        text-align:left;
        display: table-cell !important;
        background: RGBa(255,255,255,0.3) !important;
        -moz-appearance: -moz-win-glass !important;
        opacity: 0.99 !important;}

.notification.outset{
        border: 1px solid #898c95 !important;}

.notification-inner.outset{
        margin: 0px 0px 0px 0px !important;
        padding: 0px 0px 0px 0px !important;}

#addonsMsg notification button{
        margin: 0px 0px 0px -10px !important;
        padding: 0px 0px 0px !important;}


}
