css:
@-moz-document domain("rprepository.com") {body {
    margin: 0;
    font-weight: 400;
    line-height: 1.6;
    color: #fff7f5;
    text-align: left;
    background-color: #0e0b11;
}

header {
    background: #000;
}

header .navbar li.active-sub>a {
    color: #05e7fc;
}

header a:focus, header a:hover {
    color: #fff7f5;
    background: #3e1429;
}

a {
    color: #aa5599;
}

a:focus, a:hover {
color: #05e7fc;
    text-decoration: none;
}

.content a {
    text-decoration: none;
}

.dropdown-menu {
    margin-top: 0;
    background: #0e0b11;
    border-color: #3e1429;
}

.dropdown-menu a {
    color: #05e7fc;
}

.dropdown-menu a:hover {
    background: #3e1429;
    color: #fff7f5;
}

.dropdown-menu a.dropdown-item.active {
    background: #000000;
    color: #fff7f5;
}

ul.notifications > li {
    margin-bottom: 24px;
    padding-bottom: 24px;
    border-bottom: 1px solid #05e7fc;
}

select {
    word-wrap: normal;
    background: #131011;
    color: #fff7f5;
    border: #1d1d1d;
}

.form-control {
    background: #403f3e;
    color: #fff7f5;
    border-color: #131011;
}

.selectize-dropdown,
.selectize-dropdown.form-control {
    background: #403f3e;
}
.selectize-dropdown,
.selectize-dropdown.form-control {
    height: auto;
    padding: 0;
    margin: 2px 0 0;
    z-index: 1000;
    background: #131011;
    border: 1px solid #ccc;
    border: 1px solid rgba(0, 0, 0, .15);
    border-radius: 4px;
    box-shadow: 0 6px 12px rgba(0, 0, 0, .175);
}
.form-horizontal .form-control {
    width: 50%;
    display: inline-block;
}
.form-control {
    background: #403f3e;
    color: #fff7f5;
    border-color: #131011;
}
.selectize-dropdown {
    position: absolute;
    z-index: 10;
    background: #fff;
    margin: -1px 0 0;
    border: 1px solid #d0d0d0;
    border-top: 0;
    box-sizing: border-box;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .1);
    border-radius: 0 0 4px 4px;
}
.selectize-dropdown,
.selectize-input,
.selectize-input input {
    color: #333;
    font-family: inherit;
    font-size: inherit;
    line-height: calc(2.35rem + 2px);
    -webkit-font-smoothing: inherit;
}

.selectize-control.single .selectize-input.full.input-active,
.selectize-control.single .selectize-input.full:focus,
.selectize-control.single .selectize-input.input-active,
.selectize-control.single .selectize-input:focus {
    background: #3e1429;
    color: #fff7f5;
    border-color: #fbf3f1;
}

.navcontainer #alltabs #pagetabs {
    background: #3e1429;
    list-style-type: none;
    margin: 0;
    padding: 5px 20px;
}

.navcontainer #alltabs #pagetabs ul.nestedPages {
    display: none;
    position: absolute;
    z-index: 100;
    padding-left: 0;
    background-color: #0e0b11;
    border: 1px solid #3e1429;
}

.navcontainer #alltabs #pagetabs ul.nestedPages a {
    text-decoration: none;
    background: none;
    color: #05e7fc;
    padding: 5px 10px;
    display: block;
}

.navcontainer #alltabs #pagetabs ul.nestedPages a:focus, .navcontainer #alltabs #pagetabs ul.nestedPages a:hover {
    background: #000000;
    color: #fff7f5;
}

header .navbar li.active {
    background: #3e1429;
}

ul.topiclist li.highlight {
    background: #000;
}

ul.topiclist li {
    display: block;
    padding: 0 1rem 1rem;
    clear: both;
    border-bottom: 1px solid #0e0b11;
}

.selectize-input, .selectize-input.full {
    background: #000;
    color: #fff7f5;
}
.selectize-input {
    border-color: #737373;
}

div.useractivity {
    padding: 1rem;
    background: #131011;
    clear: both;
    margin-top: 2rem;
}

#userprofile .kudos {
    margin-bottom: 2rem;
    padding-bottom: 2rem;
    border-bottom: 1px solid #131011;
}

#playerInfoBar .displaycase {
    padding-top: 2rem;
    border-top: 1px solid #131011;
}

#profile-builder .editorColumn {
    border: 1px solid #3e1429;
    padding: 0;
}

#profile-builder #widgetDrawer a.potential-widget,
.btn-primary {
    background: #05e7fc;
    border-color: #05e7fc;
    color: #000000;
}

#profile-builder #widgetDrawer a.potential-widget:focus, #profile-builder #widgetDrawer a.potential-widget:hover, .btn-primary:focus, .btn-primary:hover {
    background: #3e1429;
    border-color: #3e1429;
    color: #fff7f5;
}

#profile-builder .editorColumn .columnheader {
    background: #3e1429;
    color: #fff7f5;
    padding: .5rem 1rem;
    text-align: right;
    margin-bottom: 1rem;
}

#profile-builder .widget {
    border: 1px solid #3e1429;
    margin-bottom: 1rem;
    background: #000;
}

#profile-builder .widget .titlebar {
    background: #3e1429;
    color: #fff7f5;
    padding: .5rem 1rem;
}

#profile-builder #widgetDrawer a.potential-widget:disabled,
.btn-primary:disabled {
    background-color: #000;
    color: #fff7f5;
    border-color: #131011;
}

ul.friendships li {
    padding: 0 0 1rem;
    margin-bottom: 1rem;
    border-bottom: 1px solid #ffffff;
    clear: both;
}

ul.forumlist li.topbar {
    background: #000;
    color: #fff7f5;
    padding-bottom: 0;
}
