    border: 1px solid #000;
    background: #000;
}

.forumpost .topbar {
    background: #3e1429;
    width: 100%;
    clear: both;
    padding: .25rem .5rem;
    margin: 0 0 1.5rem;
}

.forumpost.ooc {
