}

.nav-tabs, .nav-tabs .nav-item.show .nav-link, .nav-tabs .nav-link.active, .nav-tabs .nav-link:active, .nav-tabs .nav-link:focus, .nav-tabs .nav-link:hover {
    border-color: #05e7fc;
}

.markItUpHeader {
    color: #05e7fc;
}

.markItUpContainer {
    border: none;
    background: none;
    padding: 5px 5px 2px;
    font-size: 14px;
}

.markItUpEditor {
    font-size: 17px;
    padding: 5px;
    background: #000000;
    color: #fff7f5;
    border-color: #3e1429;
    margin: 0 1% 10px;
    width: 98%;
    height: 200px;
    clear: left;
    display: block;
    line-height: 18px;
    overflow: auto;
    box-sizing: border-box;
}

.form-control:focus {
    background: #131011;
    color: #fff7f5;
    border-color: #fbf3f1;
}

.conversation-message.collapsed {
    background: rgba(0, 0, 0, 0.2);
    font-size: .9em;
}

.conversation-message.collapsed:hover {
    background: #3e1429;
}

.conversation-message {
    padding: 1rem;
    border-radius: 5px;
    border: 1px solid #3e1429;
    background: #000;
    margin-bottom: 1rem;
}

.card,
.notice {
    background: #000;
    color: #fff7f5;
}

.topicsubscriptions li {
    margin-bottom: 1rem;
    padding-bottom: 1rem;
    border-bottom: 1px solid #fbf3f1;
}

.searchsection {
    background: #131011;
    color: #fff7f5;
    margin-top: 3rem;
    padding: .5rem 1rem;
    font-size: 1.3em;
}

.selectize-input.full.input-active, .selectize-input.full:focus, .selectize-input.input-active, .selectize-input:focus {
    background: #000;
    color: #fff7f5;
    border-color: #ffffff;
}

ul.inventory li img {
    display: block;
    width: 217px;
    margin: auto;
    border: 1px solid #4c4848;
}

ul.inbox li {
    margin: 0;
    padding: 5px 20px;
    line-height: 1.1em;
    border-bottom: 1px solid #0e0b11;
    vertical-align: middle;}

.bbcode_quote {
    border: 1px solid #131011;
    margin-top: 14px;
    margin-bottom: 14px;
    display: block;
    overflow: hidden;
}

.bbcode_quote_head {
    background: #0e0b11;
    color: #fff7f5;
    padding: 5px;
}

#chatapp .message {
    display: -webkit-box;
    display: flex;
    background: #000;
    clear: both;
    width: 99%;
    margin: auto;
}

.conversation-message .diceresults, .forumpost .diceresults {
