@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("vk.com"), domain("vkforms.ru"){
/*текст  var(--steel_gray_400) стало red*/
.like_views,
.nim-dialog .nim-dialog--who {
    color: red;
}

/* цвет был --gray_400 - стало #c65505; темно-оранжевыйй */
.im-mess-stack .im-mess-stack--content .im-mess-stack--tools a, 
a.bp_date,
.AppsCatalogCard__category,
.audio_w_covers .audio_row .audio_row__performers a,
.audio_page_player2 .audio_page_player_duration,
.audio_page_player2 .audio_page_player_title_performer .artist_link,
.audio_friend_audios_count,
.audio_friend_now,
.audio_row .audio_row__duration,
.bookmarks_empty_feed .bookmarks_empty_feed__text,
div.profile_online,
.docs_item_additional_info,
.GamesCatalogCard__link,
.group_edit_hint,
.header_right_link,
.im-page--members,
.left_menu_nav,
.MarketCatalogGroup__counter,
.module_header .header_count,
.no_current_info,
#pv_tags,
.pg_in,
.post_date,
.post_date .post_link,
.post_date .wall_text_name_explain_promoted_post,
.post_header_info .explain,
.post_video_views_count,
.post_video_views_count a,
.post_header_info .explain,
.page_block_header_count,
.photos_container .photos_period_delimiter,
.right_list_link,
.right_list_field .group_link,
.ShortVideoTagListItem__infoText,
.thumbed_link__subtitle,
.tickets_header_info,
.ts_contact_info,
.ui_tab_count,
.ui_crumb_count,
.video_item_info,
.right_list_field,
.GroupsRecommendationsBlock__listRowFieldStatus,
.wall_module .wd_lnk {
    color: #c65505;
}

/*текст --steel_gray_500 стало #df34ba фуксия*/

.im-mess-stack .im-mess-stack--content .im-mess-stack--pname, 
.ProfileActions .page_actions_expanded .page_actions_item,
.GamesCatalogNav__balanceLink,
.nim-dialog .nim-dialog--date {
    color: #df34ba;
}
/*слово Поиск цвет*/
body.new_header_design .TopSearch__input::placeholder,
body.new_header_design .TopSearch .input_back_content {
    color: #df34ba;
}

/*текст --gray_500 стало #7b4119 темнокоричневый*/

.im-page .im-page--history-new-bar, 
.profile_info .label,
.page_counter .label,
.post_action_btn,
input.text::placeholder,
input.text ~ .placeholder .ph_content,
input.search::placeholder,
input.search ~ .placeholder .ph_content,
input.big_text::placeholder,
input.big_text ~ .placeholder .ph_content,
input.dark::placeholder,
input.dark ~ .placeholder .ph_content,
.fakeinput::placeholder,
.fakeinput ~ .placeholder .ph_content,
div[contenteditable=true]::placeholder,
div[contenteditable=true] ~ .placeholder .ph_content,
textarea::placeholder,
textarea ~ .placeholder .ph_content,
input.text::-webkit-input-placeholder,
input.text ~ .placeholder .ph_content,
input.search::-webkit-input-placeholder,
input.search ~ .placeholder .ph_content,
input.big_text::-webkit-input-placeholder,
input.big_text ~ .placeholder .ph_content,
input.dark::-webkit-input-placeholder,
input.dark ~ .placeholder .ph_content,
.fakeinput::-webkit-input-placeholder,
.fakeinput ~ .placeholder .ph_content,
div[contenteditable=true]::-webkit-input-placeholder,
div[contenteditable=true] ~ .placeholder .ph_content,
textarea::-webkit-input-placeholder,
textarea ~ .placeholder .ph_content,
.PhotoTagCardOwner__subtitle {
    color: #7b4119;
}

/*текст var(--steel_gray_540); на #ff3347*/
.like_button_count {
    color: #ff3347;
}

.page_actions_dd_label, .page_actions_header_inner {
    color: white;
}

/*текст --gray_600: #656565; стало #000 черный*/
.page_list_module .group_desc,
.page_list_module .people_desc,
.page_list_module .people_extra,
.ui_tabs .ui_tab,
.ui_tab,
.ui_rmenu_subitem,
.nim-dialog .nim-dialog--text-preview,
.nim-dialog .nim-dialog--preview,
.friends_common_label,
.friends_field,
.group_row_info {
    color: #000;
}

/* Текст --blue_600: #2a5885 и --link-color: var(--blue_600); стало темнокрасный*/
.ui_rmenu_item,
.ui_rmenu_subitem,
.page_counter .count,
.page_counter:hover .label,
.im-mess-stack .im-mess-stack--content .im-mess-stack--pname > a,
.im-page-pinned--name,
.im-right-menu .im-right-menu--count,
.wall_post_more,
.wall_reply_more,
.wall_copy_more,
.wall_post_attaches_more,
.deep_active .post_replies_header .post_replies_reorder,
.deep_active.wall_module .post_replies_header .post_replies_reorder,
.header_side_link,
.header_side_link a,
.flat_button.secondary.button_light,
.box_controls .flat_button.secondary,
.wide_column .topics_module .topic_title,
.topics_module .topic_inner_link,
.page_doc_row .page_doc_title,
.page_actions_item,
a, .ts_contact_name,  
.ui_actions_menu_item 
{
    color: #bf0000;
}

.pg_lnk_sel .pg_in {
    color: #bf0000;
}

.post_video_title {
    color: #bf0000 !important;
}

/*текст --steel_gray_600 на #bf6525 */
.nim-dialog:not(.nim-dialog_deleted):hover .nim-dialog--text-preview,
.nim-dialog:not(.nim-dialog_deleted):hover .nim-dialog--preview,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_hovered .nim-dialog--text-preview,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_hovered .nim-dialog--preview,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_unread.nim-dialog_classic .nim-dialog--text-preview,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_unread.nim-dialog_classic .nim-dialog--preview {
    color: #bf6525;
}

/* Текст --gray_600 #bf0000*/
a.ui_crumb {
    color: #bf0000;
}

.wide_column .topics_module .topic_info {
    color: #c65505;
}

/*текст - --light_blue_700 замена #ff3347*/
.nim-dialog:not(.nim-dialog_deleted):hover .nim-dialog--preview.nim-dialog--preview-attach,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_hovered .nim-dialog--preview.nim-dialog--preview-attach,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_unread.nim-dialog_classic .nim-dialog--preview.nim-dialog--preview-attach {
    color: #ff3347;
}

.page_photo.page_action_menu_groups .page_actions_item {
    color: #bf0000;
}

/*имя справа цвет текста и цвет песни которая играет*/
.top_nav_link, .top_audio_player_title {
    color: white !important;
}

/*текст --blue_420 замена #a05711*/
.stories_groups_block_stories_empty .page_module_upload {
    color: #a05711;
}

/*реклама*/
#ads_left {
    display: none !important;
}

