css:
@-moz-document url-prefix("https://app.idorecall.com/recalls"), regexp("https://app.idorecall.com/groups/.+/recalls.*") {
/* Recalls */
.meta-container, .recalls-list-wrapper, .tags-overlay-container, .recall-item:hover, .columns-wrapper.meta-opened .meta-info-col, .tags-overlay-container .recalls .tag:hover{ background-color: #202020 !important;}
.recall-item .recall-title, .fixed-header .close-desktop, .form .form-group > .control-label, .controls-panel-record, .mat-checkbox-layout, .form-row input, .form .form-group.wrap-metacognition, .adjust-control, .mat-checkbox-disabled .mat-checkbox-label{ 
    color: #fff !important;
}
.filter-btn, .tags-overlay-container, .search-criteria{ color: #76808F !important;}
}

@-moz-document url-prefix("https://app.idorecall.com/") {
/* General */
body{ background-color: #202020 !important;}
}

@-moz-document url-prefix("https://app.idorecall.com/library"), regexp("https://app.idorecall.com/groups/.+/library") {
/* Library */
.meta-container, .library-list-wrapper, .tags-overlay-container, .tag:hover{ background-color: #202020 !important;}
.filter-btn, .tags-overlay-container, .search-criteria{ color: #76808F !important;}
.title, .create-note, .meta-container-bottom, .share-link{ color: #fff !important;}
}

@-moz-document url-prefix("https://app.idorecall.com/practice") {
/* Practice */
html, body, .practice-footer{ background-color: #202020 !important;}
.achieve-item-wrap, .progress-bar-determinare{ color: #fff !important;}
}

@-moz-document url("https://app.idorecall.com/groups") {
/* Groups */
.classes-default-item-list{ background-color: #202020 !important;}
.classes-default-item-list{ color: #fff !important;}
}

@-moz-document url-prefix("https://app.idorecall.com/groups") {
/* Groups left panel */
.meta-container, .tags-overlay-container{ background-color: #202020 !important;}
.mat-expansion-panel-header.group .group-info, .menu .menu-item{ color: #76808F !important;}
.tags-overlay-container .head-count-number{ color: #fff !important;}
}

@-moz-document regexp("https://app.idorecall.com/groups/.+/about") {
/* Group - About */
.content .group{ background-color: #202020 !important;}
.content .group{ color: #fff !important;}
.qr-text{ color: #76808F !important;}
}

@-moz-document regexp("https://app.idorecall.com/groups/.+/members") {
/* Group - Members */
.group-members-wrapper, .group-members-item:hover{ background-color: #202020 !important;}
.qr-ref, .group-members-item, .group-members-item .group-members-item-title{ color: #fff !important;}
.filter-btn{ color: #76808F !important;}
}

@-moz-document regexp("https://app.idorecall.com/groups/.+/leaderboard") {
/* Group - Leaderboard */
.columns-wrapper .items-list-col{ background-color: #202020 !important;}
.columns-wrapper .items-list-col, .mat-select-value, .mat-select-arrow{ color: #fff !important;}
.empty-data{ color: #76808F !important;}
}

@-moz-document regexp("https://app.idorecall.com/groups/.+/settings") {
/* Group - Settings */
.columns-wrapper.main-group .items-list-col{ background-color: #202020 !important;}
.columns-wrapper .items-list-col{ color: #fff !important;}
.separate{ border-color: #fff !important;}
}
