/*---- COMBINE STOP/RELOAD BUTTONS ----*/
#stop-button[disabled] {display: none;}
#stop-button:not([disabled]) + #reload-button {display: none;}

/*---- NO THROBBER FOR CURRENT TAB ----*/
.tabbrowser-tab[selected="true"] .tab-icon-image {
opacity: 0 !important;}

