/*----  LOADING BAR ----*/
/* HIDE */
#urlbar[fission="fusion"] > #statusbar-icon > .progress-bar, /* FISSION PRGRESS */
#urlbar[progress]:not([progress="100"]) #feed-button, /* RSS ICON */
#urlbar[progress]:not([progress="100"]) [id*="isRitL"] /* READITLATER */
{display: none !important;}

#stop-button:not([disabled]) {
list-style-image: none !important;
list-style-image: url("chrome://global/skin/icons/loading_16.png") !important;
-moz-image-region: rect(0, 0, 0, 0) /*rect(0, 16px, 0, 16px)*/ !important;
background: url("data:image/png;base64,") no-repeat !important;
padding: 3px 84px 3px 35px !important;
margin: -3px -7px -3px -32px/*2px*/  !important;
position: fixed !important;
z-index: 1000 !important;}

#stop-button:hover:active {
background: url("data:image/png;base64,") no-repeat !important;}

/*---- LOADING BAR FINISHING ----*/
#urlbar[progress]:not([progress*="6"]):not([progress*="7"]):not([progress*="8"]):not([progress*="9"]) #stop-button:not([disabled]) {
/* white spinner */
list-style-image: url("data:image/png;base64,") !important;
padding-top: 2px !important;

background: url("data:image/png;base64,") no-repeat !important;}

#urlbar[progress]:not([progress*="6"]):not([progress*="7"]):not([progress*="8"]):not([progress*="9"]) #stop-button:not([disabled]):hover:active {
background: url("data:image/png;base64,") no-repeat !important;}


/*---- WINDOW INACTIVE ----*/
window:not([active="true"]) #stop-button:not([disabled]) {
background: url("data:image/png;base64,") no-repeat !important;}

window:not([active="true"]) #urlbar[progress]:not([progress*="6"]):not([progress*="7"]):not([progress*="8"]):not([progress*="9"]) #stop-button:not([disabled]) {
background: url("data:image/png;base64,") no-repeat !important;}

