@-moz-document domain("youtube.com") {
/* Creator: pozxyyy */
/* Compact Tube Creator: jakill02 */
/* Created: Dec_25_2020 */

/* Playlistheader */
/* HeaderBackground */

.header.ytd-playlist-panel-renderer {
    background-color: #222;
    padding: 16px 16px 0 24px;
    -ms-flex: none;
    -webkit-flex: none;
    flex: none;
}

/* NameofPlayist */

.title.ytd-playlist-panel-renderer {
    color: #fff;
    margin-bottom: 8px;
    white-space: var(--ytd-subheadline-one-line-ellipsis_-_white-space);
    text-overflow: var(--ytd-subheadline-one-line-ellipsis_-_text-overflow);
    display: var(--ytd-subheadline-one-line-ellipsis_-_display);
    max-height: var(--ytd-subheadline-one-line-ellipsis_-_max-height);
    overflow: var(--ytd-subheadline-one-line-ellipsis_-_overflow);
    font-size: var(--ytd-subheadline-one-line-ellipsis_-_font-size);
    font-weight: var(--ytd-subheadline-one-line-ellipsis_-_font-weight);
    line-height: var(--ytd-subheadline-one-line-ellipsis_-_line-height);
    letter-spacing: var(--ytd-subheadline-one-line-ellipsis_-_letter-spacing);
}

/* YoutubeText */

ytd-playlist-panel-renderer[collapsible] .publisher.ytd-playlist-panel-renderer {
    font-size: 1.2rem;
    font-weight: 400;
    line-height: 1.5rem;
    color: #fff;
}

}
