css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("jtas.sjgames.com") {
  a, a:link, a:active, a:visited {
    color: #53777A; 
    text-decoration:none;
  }
  body {
    background: #ffffff; 
    color: #202020;
    font: 14px Tahoma, Helvetica, Sans-Serif;
    line-height:1.2em;
    margin:0;
    padding:0;
  }

  blockquote {
    width:800px;
    margin:1em auto; 
    -moz-column-width:288px; 
    -moz-column-gap:19px;
  }

/* Headings
-------------------------------------------------------------- */

h1,h2,h3,h4,h5,h6 { font-weight: normal; color: #990033;   font-family: Zrnic, Impact; margin-bottom:0.5em; }
h1 { font-size: 2.5em; line-height: 1;}
h2 { font-size: 2em;}
h3 { font-size: 1.5em; line-height: 1;}
h4 { font-size: 1.2em; line-height: 1.25;}
h5 { font-size: 1em; font-variant: small-caps;}
h6 { font-size: 1em; font-weight: bold; }

p { 
    margin: 0.5em 0 1em 0;
    text-align:justify;
  }

center img {display: none;}

blockquote blockquote {
    border-left: 5px solid #D95B43;
    background: #ECD078;
    color: #542437;
    font-style: italic; 
    margin: 0 0 0 1em; 
    width:auto;
    padding:1em; 
    -moz-column-count:1
  }
  ul, ol {margin: 0; padding: 0;}
  ul {list-style: square inside; }
  ol {list-style: lower-roman inside; }
  li {text-indent: -1em; padding-left: 1em; margin-bottom:0.5em}
}
