css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("spore.com") {

body { background-image:url("http://www.spore.com/static/war/images/global/tiling_glyph_background.jpg") !important; }

#content { background:none !important; margin-bottom:25px !important; }

#sporeTabContent, #galacticAdvTabContent, #rightContentDiv { background:#FFF !important; }

#rightContentDiv, #middleCol { -moz-border-radius:9pt; border:1px solid #AAA; }

#middleCol { background:#FFF; padding:.75em .75em 2em !important; margin-top:1.5em !important; }

#middleCol, #pubProfileContainer, #leftCol, #rightCol { min-height:0 !important; }

#sporeTabContent { -moz-border-radius-bottomleft:9pt; -moz-border-radius-bottomright:9pt; }

#identityDiv { margin-left:25px !important; margin-top:10px !important; min-height:140px !important; }

#myAvatarcustomframe { left:25px !important; top:12px !important; }

#featuredAdvDiv { min-height:205px !important; }
#featuredAdvContent { padding:10px !important; width:auto !important;  }
#featuredAdvDataDiv { clear:both; padding-top:5px !important; margin:0 !important; border:0 !important; height:15px !important; }
#requiresGalAdv { display:none; }
#featuredCreationsDiv { height:auto !important; }
.Topnewstext { padding-right:10px !important; }

#sporeTabDiv { top:167px !important; }

#nameTaglineDiv { width:auto !important; }
.flag-tagline { text-align:right; padding-right:1em; }

#pubProfileContainer .avatar { margin-left:11px !important; }

#selected-avatar-frame { left:12px !important; top:11px !important; }

#gamesOwnedDiv { text-align:center !important; }
#gamesOwnedDiv img { display:inline !important; }

#asset-comments { min-height:0 !important; }

/* Crappy forum software requires some of the following rules to be a little...exotic? */

.forumline #signContainer,
.forumline hr,
.forumline .gensmall, 
.forumline img[alt="*"],
.spacerow,
.postinfo .action img[alt="*"],
.postinfo .action [onclick],
.postinfo .action .nav,
.postinfo .subject,
.forumline td[height="28"]
	{ display:none !important; }

#post td[height="28"] { display:block !important; }

.genmed { font-size:11pt !important; text-align:center !important; }
.forumline { margin:.5em 0 !important; background:#FFF !important; }
.forumline td { background:#FFF !important; }

.postinfo .date { padding:4px !important; }
}
