css:
@-moz-document domain(app.fluencia.com)
{
body:not(.body-fresh) .quiz-done-container,
.quiz-done-stats-correct-pct,
.quiz-done-stats-questions-num,
.app-body,
body:not(.body-fresh) .feedback .feedback-body,
body:not(.body-fresh) .input .type-area textarea, body:not(.body-fresh) .input .type-area input[type="text"],
body:not(.body-fresh) h1.quiz-done-callout-text, body:not(.body-fresh) p.quiz-done-callout-text,
body:not(.body-fresh) .quiz-done-lesson-name,
body:not(.body-fresh) .quiz-done-container, .quiz-done-stats-correct-pct, .quiz-done-stats-questions-num, .app-body, body:not(.body-fresh) .feedback .feedback-body,.stat-box-number,
.sidebar-info-title,
.unit-name,
body:not(.body-fresh) .input .number-list,
body:not(.body-fresh) .input .number-list .number,
.level-list-item-title,
.level-list-item-count,
.level-list-item-description,
.grammar-tags-content,
.unit-list-item-title,.unit-list-item-count,
.unit-list-item-description,
body:not(.body-fresh) .input .input-second-attempt,
.progress-widget-text-description,
.modal-general .modal-title,
.modal-general .modal-body {
    color: #ddd !important;
}
.plans-list-feature-title,
.plans .plan-detail .plan-month,
.main-holder h1,
.skill-list .number-list a,
#word.word h5,
#word.word h6 {
    color: #ccc;
}
.prompt .text.img-holder-text .cell.image-label {
    color: #eee;
}
.skill-list .number-list .skills-item-skill-incomplete {
    color: #aaa;
}
a:hover,
a:active,
a:focus {
    color: hsl(200, 100%, 35%);
}
body:not(.body-fresh) .quiz-done-actions,
.main-holder,
.app-sidebar-box-border {
    background: rgba(0,0,0,0.45); /* 333 */
}
body:not(.body-fresh) .help-bar-contents {
    background: rgba(0,0,0,0.2); /* #444 */
    box-shadow: 0 1px 0 transparent;
}    
.quiz-loading .quiz-loading-tip {
    background: rgba(0,0,0,0); /* #444 */
}
.app-main, .app-main-next {
    background-color: transparent;
    border: 10px solid rgba(0,0,0,0.2);
    box-shadow: 0 1px 0 transparent;
}
.app-body {
    min-height: 100vh;
    backdrop-filter: brightness(.25) blur(3px) saturate(.7);
}
.image img {
    filter: brightness(.92);
}
body:not(.body-fresh) .input .type-area {
    color: #ddd;
    border-top: 1px solid #888;
    border-bottom: 1px solid #888;
    border-left: 1px solid #999;
    border-right: 1px solid #999;
}
body:not(.body-fresh) .prompt-vocab .image-label,
body:not(.body-fresh) .quiz-done-container, .quiz-done-stats-correct-pct, .quiz-done-stats-questions-num, .app-body, body:not(.body-fresh) .feedback .feedback-body {
    color: #ddd;
}
body:not(.body-fresh) .answer-area {
    background: hsla(2, 85%, 40%, .15);
    border: solid hsla(2, 85%, 40%, .2);
    color: #ddd;
    /*  background: #444;
    border: solid #777;
    color: #ddd; */
}
#header {
    background: hsla(197, 100%, 25%, .5); /* #006993 */
    border-bottom: none; /* 1px solid #006993 */
    box-shadow: none; /* 0 1px 0 #0079a8 */
}

#header:after,
.help-site-link,
body:not(.body-fresh) .help-bar-completed-slides,
.unit-list-item-locked:hover .unit-list-item-overlay {
    background: #17384f; /* #006993 */
}
.btn-share-email {
    background: #006993;
    border: 1px solid #006993
}
.app-sidebar-box {
    background-color: transparent; /* #555 */
    border: 10px solid rgba(0,0,0,0.2); /* 10px solid #555 */
    border-radius: 5px;
}
.images-area .image {
    border: 5px solid #777;
}
.input .image:hover {
    border: 5px solid #538bb4;
}
.img-holder .img-holder-text {
    background: rgba(0,0,0,0.6);
}
.images-area .image .image-label {
    background: rgba(0,0,0,0.7);
    color :#ddd;
}
.images-area .choice-success:hover .image-label,
.images-area .choice-success:hover .image-label span{
    color: #333;
}
.skill-list .number-list .skills-item-skill:hover,
.level-list-item:hover,
.unit-list-item:hover {
    background-color: #17384f; /* #444 */
}
.prompt .info strong, .prompt .dialogue strong {
    color: #b234b2;
}
body:not(.body-fresh) .input .number-list li:first-child,
body:not(.body-fresh) .input .number-list .choice {
    border-top: 1px solid #333;
}
body:not(.body-fresh) .input .number-list li {
    border-bottom: 1px solid #333;
    border-left: 1px solid #333;
    border-right: 1px solid #333;
}
body:not(.body-fresh) .input .number-list li:hover {
    background-color: hsla(197, 100%, 25%, .5);
}
body:not(.body-fresh) #quiz-question .tip {
    background-color: hsla(205, 60%, 40%, .5);
    border-radius: 4px;
}
body:not(.body-fresh) .feedback .feedback-item-0 {
    background-color: hsl(0, 55%, 30%);
}
body:not(.body-fresh) .feedback .feedback-item-1 {
    background-color: hsl(285, 50%, 35%);
    margin: 5px 0px;
}
body:not(.body-fresh) .verb-table .text-highlighted,
body:not(.body-fresh) .feedback-note {
    color: hsl(55, 50%, 80%);
}
body:not(.body-fresh) .input .input-second-attempt {
    background: #444;
    border: solid #888;
    color: #ddd;
}
body:not(.body-fresh) .input .type-area.choice-warning {
    border: 1px solid #888;
}
body:not(.body-fresh) .input .sb-response {
    background-color: #555;
    border: 1px solid #888;
}
body:not(.body-fresh) .input .input-builder-option {
    background: #555;
    border: 1px solid #888;
    box-shadow: 0 1px 0 #aaa inset, 0 -1px 0 #aaa inset;
    color: #eee;

}
.progress-widget-svg .progress-widget-foreground {
    fill: #333;
}
.progress-widget-svg .progress-widget-background {
    fill: #ddd;
}
.plans3 .plan3-billing-info .credit_card .payment-info-row .field input[type="text"] {
    background-color: #555;
    color: #ddd;
}
.plans3 .plan3-billing-info .credit_card .payment-info-row:nth-of-type(1) input[type="text"] {
    border-top: 1px solid #777;
}
.plans3 .plan3-billing-info .credit_card .payment-info-row .field input[type="text"] {
    border: 1px solid #777;
}
.recurly .field .placeholder {
    color: #aaa;
}
.recurly .field.focus .placeholder {
    color: #777;
}
hr {
    border-top: 1px solid #444;
    border-bottom: 1px solid #555;
}
.tip-text hr {
    border-top: 1px solid hsl(205, 55%, 33%);;
    border-bottom: 1px solid hsl(205, 55%, 36%);;
}
.menu ul li {
    border-top: 1px solid #777;
    background-color: #444;
}
.dropdown-menu>li>a {
    color: #ddd;
}
.menu ul {
    border: 2px solid #006993;
    background: #555;
}
.menu>li:hover, .menu>li.menu-active {
    background-color: #0079a8;
}
.menu ul li:hover>a {
    text-decoration: none;
    color: #eee;
    background-color: #555;
    background-image: none;
    filter: none;
}
.skill-list .number-list .skills-item-skill .skill-pearl .background,
.skill-list .number-list .skills-item-skill-incomplete:hover .skill-pearl .background,
.skill-pearl-incomplete .background {
    fill: #333;
}
.unit-list-item:hover .skill-pearl-incomplete .background {
    fill: #555;
}
.table-bordered {
    border: 1px solid #888;
}
.table-bordered th, .table-bordered td {
    border-left: none;
}
.table th, .table td {
    border-top: 1px solid #888;
}
.voice-opt-in-classic .voice-opt-in-footer {
    background-color: transparent; /* #444 */
    border-top: 1px solid #999;
}
.voice-area tspan {
    fill: #ddd;;
}
body:not(.body-fresh) .input .number-list-disabled .choice-error, body:not(.body-fresh) .input .number-list-disabled .choice-success {
    background-color: #444;
}
.images-area .choice-success:hover .image-label {
    background: hsl(80, 85%, 40%);
    color: #fff;
}
.images-area .choice-error:hover .image-label {
    background: hsl(2, 85%, 40%);
    color: #fff;
}
.images-area .image .image-label span,
.images-area .choice-success:hover .image-label, .images-area .choice-success:hover .image-label span {
    color: #ddd;
}
body:not(.body-fresh) .help-bar-icons a {
    background: #444;
    color: #777;
}
body:not(.body-fresh) .help-bar-icons .toggle-accent {
    background: #777;
    color: #444;
}
#quiz-word-overlay {
    background: #444;
}
body:not(.body-fresh) .feedback #diff-container .ins {
    color: #8bd001;
}
.modal-general .modal-holder {
    background: #333;
}
.modal-footer {
    background-color: #444;
    border-top: 1px solid #333;
    box-shadow: inset 0 1px 0 #555;
}
.modal-general {
    border: 10px solid #555;
    background: #555;
    box-shadow: 0 1px 0 #666;
}
.close,
.close:hover, .close:focus{
    color: #fff;
    text-shadow: 0 1px 0 #fff;
}
.quiz-loading .quiz-loading-activity[class*="ico-"] {
    background: #5eba66;
    box-shadow: 0 0 0 0 #777;
}
.quiz-loading .quiz-loading-activity[class*="ico-"]:hover {
    background: #5eba66;
    box-shadow: 0 0 0 0 #777;
}
[class^="ico-"], [class*=" ico-"] {
    color: #333;
    box-shadow: 0 -1px 0 0 #555 inset;
}
.smart-review-loading-footer {
    background-color: #444;
    border-top: 1px solid #555;
}
.well {
    background-color: #444;
    border: 1px solid #555;
}
body:not(.body-fresh) .alert {
    background-color: #333;
}
body:not(.body-fresh) .alert .alert-holder {
    color: #ddd;
    background: #333;
}
body:not(.body-fresh) .alert {
    border: 10px solid #555;
}
.tip .table-bordered {
    border: 1px solid hsl(205, 55%, 40%);
}
.tip .table th, .tip .table td {
    border-top: 1px solid hsl(205, 55%, 40%);
}
.search-category-list {
    background-color: #333;
}
.search-skill-item:hover {
    background-color: #444;
}
.search-view a {
    color: #ddd;
}
body:not(.body-fresh) .input .quiz-input-disabled {
    background-color: #444;
}
.profile-nav {
    color: #ddd;
}
.profile-nav-option-active {
    background-color: #444;
}
.profile-content {
    background-color: #333;
}
.profile-nav-option:hover {
    background-color: #555;
}
.profile-title {
    color: #ddd;
}
.notifications-unsubscribe .checkbox-nested .edit-label, .user-edit-form .checkbox-nested .edit-label {
    color: #ccc;
}
.js-go-prev {
    background: #111 !important;
    color: #ddd;
}
.js-go-prev:hover {
    background: #181818 !important;
    color: #ddd;
}
}
