css:
@-moz-document domain("goodblox.xyz") {
html {
    background-color: rgb(52, 0, 0);
}

body #MasterContainer {
    background-image: linear-gradient(180deg, rgba(0,0,0,1) 0%, rgba(62,1,1,1) 100%);
    background-attachment: fixed;
    background-color: black;
    background-size: 100% 1024px;
    color: white;
}

#Banner {
    background-image: linear-gradient(180deg, rgba(0,0,0,1) 0%, rgb(28, 0, 0) 100%);
}

#Header {
    color: red;
}
#Header .Navigation {
    background-color: #3e0101;
    border-bottom: solid 1px red;
    border-left: solid 1px red;
    border-right: solid 1px red;
}

#Logo img {
    content: url("https://goodblox.xyz/resources/gb.png");
}

a:link, a:visited, a:active {
  color: deepskyblue;
}

.Title {
    color:black;
}

#ProfilePane tr td {
    color:#000;
}

#ProfilePane tr td a {
    color:#1e3da4;
}


}
