css:
@-moz-document domain("sozcu.com.tr") {


body.home:not(.plus), body.detail {
    background: #121212;
}

.content-hb{
    background: #121212;
    color:#f2e7fe;
}

.content-element p {
    color: #f2e7fe;
}
.detail-content, .content-block {
    background: unset;
}
.news-footer{
        background: #292929;
    color:#f2e7fe;
}

.load_area{
    border-top: 5px solid #ff4136;
}
.news-content-share{
    display:none;
}
.related-news-box-inner{
            background: #292929;
 color:#f2e7fe;
}
.r-title:before {
    color: #ff4136;
}
.news-cat {
    color: #ff4136;
}

/* Remove Ads */

.masthead.sticky-done{
    display:none !important;
}
body > div.full-container > div.pageskin-sol.pageskin._pageskin_._pageskin._pageskin-left{
    display:none !important;
}
body > div.full-container > div.pageskin-sag.pageskin._pageskin_._pageskin._pageskin-right{
    display:none !important;
}
.sidebar-banner{
    display:none !important;
}
.outof-news-body{
    display:none !important;
}
.popup-overlay{
    display:none !important;
}
body{
    overflow:unset !important;
}
.fc-ab-root{
    display:none !important;
}

.using-cookie{
    display:none !important;
}
.fc-dialog-overlay {
        display:none !important;
}
}
