@-moz-document url-prefix("https://mint.intuit.com/overview.event") {
/* Global */
#wrapper {
    max-width: 1800px !important;
}


/* Overview */
#module-accounts .nickname,
#module-accounts a.accountName {
    width: 400px;
}

/* Widget */
/* all */
.overviewPage .module {
    max-width: 1100px !important;
}

/* Budget */
#module-budget thead th.bar {
    width: 576px !important;
}
}

@-moz-document url-prefix("https://mint.intuit.com/transaction.event") {
/* Global */
#wrapper {
    max-width: 1800px !important;
}



/* Overview */
#module-accounts .nickname,
#module-accounts a.accountName {
    width: 400px;
}





/* Transactions */
.TransactionPageView {
    width: 1800px !important;
}

.TransactionPageView #txn-column-accounts {
    width: 450px !important;
}


div.column#column-transactions {
    width: 950px !important;
}

table.transactions {
    width: 950px !important;
}



table.transactions th.date,
table.transactions td.date {
    width: 8% !important;
}



table.transactions th.description.noattachcol,
table.transactions.td.description.noattachcol,
table.transactions th.description,
table.transactions td.description {
    width: 45% !important;
}

table.transactions th.category,
table.transactions td.cat {
    width: 20% !important;
}


a#txnEdit-category_picker.noattachcol,
a#txnEdit-category_picker-multi.noattachcol,
a#txnEdit-category_picker,
a#txnEdit-category_picker-multi {
    left: 770px !important;
}
}

