css:
@-moz-document url("https://toyhou.se/"), url-prefix("https://toyhou.se/~browse/feed"), url-prefix("https://toyhou.se/~browse/popular"), url-prefix("https://toyhou.se/~library") {
 :root {
    /* Change the URL in quotes to whatever you want the bg to be. */
    --bg: url(https://64.media.tumblr.com/7b5c14ffd8a639b9b84b16838ba5e69c/tumblr_mrtnxlyWSH1qb6f0so1_1280.jpg);
}


/* FRONT PAGE*/

body {
    font-family: -apple-system,system-ui,BlinkMacSystemFont,Segoe UI,Roboto,Helvetica Neue,Arial,sans-serif;
    font-size: .9rem;
    font-weight: 400;
    line-height: 1.5;
    color: #eef4ed;
    background-color: white;
    background-image: var(--bg);
    background-size: cover;
    max-height: 100vh;
	overflow-y: scroll;
    position: relative;
    background-attachment: fixed;
}

.card-block.bg-faded {
    background-color: rgba(105, 105, 105, 0.7);
}

.card.mb-2 {
    background-color: rgba(105, 105, 105, 0.0);
}

/* NAVIGATION BAR*/

.navbar {
    background: rgba(105, 105, 105, 0.6);
}

.navbar-inverse .navbar-brand, .navbar-inverse .navbar-brand:focus, .navbar-inverse .navbar-brand:hover, .navbar-inverse .navbar-toggler, .navbar-inverse .navbar-toggler:focus, .navbar-inverse .navbar-toggler:hover {
    color: #a470ab;
}

.navbar-inverse .navbar-nav .nav-link {
    color: rgba(245, 235, 247, 0.5);
}

.navbar-inverse .navbar-nav .nav-link:focus, .navbar-inverse .navbar-nav .nav-link:hover {
    color: rgba(153, 153, 153, .75);
}

.dropdown-menu {
    position: absolute;
    top: 100%;
    left: 0;
    z-index: 1000;
    display: none;
    float: left;
    min-width: 10rem;
    padding: .5rem 0;
    margin: .125rem 0 0;
    font-size: .9rem;
    color: #fcfbf5;
    text-align: left;
    list-style: none;
    background-color: rgba(153, 153, 153, 0.3);
    background-clip: padding-box;
    border: 1px solid hsla(0,0%,100%,.15);
    border-radius: .25rem;
}

/* FOOTER BAR */

.footer {
     background: rgba(105, 105, 105, 0.3);
}


/* FRONT PAGE CHARACTERS */

.btn-primary {
    color: #fafaf6;
    border-color: #1b1b1b;
    opacity: 0.7;
}

.btn.btn-default {
    
    background-color: rgba(0, 0, 0, 0.6);
    border-color: none;
}

/* FEED & POPULAR CHARACTERS */

.list-unstyled.gallery-row.mb-0 {
    background-color: rgba(88, 88, 88, 0.8);
}

.img-thumbnail {
    padding: .25rem;
    background-color: #1c141d9e;
    border: 1px solid #545b75;
    border-radius: .25rem;
    transition: none;
}

.breadcrumb {
    padding: .75rem 1rem;
    margin-bottom: 1rem;
    list-style: none;
    background-color: rgba(105, 105, 105,.5);
    border-radius: .25rem;
}
}

