@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(sokker.org) {

body {
/* Permalink - use to edit and share this gradient: https://colorzilla.com/gradient-editor/#e2ebd0+4,f5f7f0+17,f5f7f0+69 */
background: #e2ebd0; /* Old browsers */
background: -moz-linear-gradient(top,  #e2ebd0 4%, #f5f7f0 17%, #f5f7f0 69%); /* FF3.6-15 */
background: -webkit-gradient(linear, left top, left bottom, color-stop(4%,#e2ebd0), color-stop(17%,#f5f7f0), color-stop(69%,#f5f7f0)); /* Chrome4-9,Safari4-5 */
background: -webkit-linear-gradient(top,  #e2ebd0 4%,#f5f7f0 17%,#f5f7f0 69%); /* Chrome10-25,Safari5.1-6 */
background: -o-linear-gradient(top,  #e2ebd0 4%,#f5f7f0 17%,#f5f7f0 69%); /* Opera 11.10-11.50 */
background: -ms-linear-gradient(top,  #e2ebd0 4%,#f5f7f0 17%,#f5f7f0 69%); /* IE10 preview */
background: linear-gradient(to bottom,  #e2ebd0 4%,#f5f7f0 17%,#f5f7f0 69%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
background-attachment: fixed;
/* font-size: 1.65em; */
}

#indexHeadContainer
{
margin-top: -10px;
display: none; /* block */
height: 300px;
min-height: 300px;
max-width: 960px;
position: fixed;
right: 585px;
top: 0px;
z-index: 9;
}

#bar-main-side
{
background-image:url("https://files.sokker.org/static/pic/logo-shade.png");   
background-repeat: no-repeat;
background-origin: content-box;
background-position: -20px -10px;
}

#bar-main-side:hover
{
z-index: 999;
max-width: 160px;
}

#indexMiddleContainer
{
margin-top: 1px;
z-index: 99;
}

#indexMiddle
{
margin-top: 10px;
z-index: 99;
}

