css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@namespace html url(http://www.w3.org/1999/xhtml);
@-moz-document url("chrome://mozapps/content/extensions/extensions.xul") {


/******** Glass Window ********/

#extensionsManager {
        background: RGBa(255,255,255,0.2) !important;
        -moz-appearance: -moz-win-glass !important;
        opacity: 0.99 !important;}

#extensionsBox{
        -moz-appearance: -moz-win-glass !important;
        background: RGBa(255,255,255,0.0) !important;
        margin: 10px 0px 0px !important;
        padding: 0px 0px 0px !important;
        opacity: 0.99 !important;}

#styles-container{
        -moz-appearance: -moz-win-glass !important;
        background: RGBa(255,255,255,0.0) !important;
        margin: 0px 0px 0px !important;
        padding: 0px 0px 0px !important;
        opacity: 0.99 !important;}


#topStackBar .viewSelector.chromeclass-toolbar {
        border-bottom: 1px solid #898c95 !important;}

.viewButtonLabel {
        opacity: 1.00 !important;}

/******** Glass Notifications ********/

#addonsMsg notification {
        text-align: justify !important;
        display: table-row !important;
        font-style: italic!important;
        background: RGBa(255,255,255,0.2) !important;
        -moz-appearance: -moz-win-glass !important;
        opacity: 0.99 !important;}

#addonsMsg notification hbox{
        -moz-column-width: 10000px;}

.notification-inner.outset{
        display: block !important;
        margin: 2px 2px 0px 2px !important;
        padding: 0px 0px 0px 0px !important;}

#addonsMsg notification button{
        margin: 0px 0px 0px 0px !important;
        padding: 0px 0px 0px !important;}



}
