css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@namespace html url(http://www.w3.org/1999/xhtml);
@-moz-document url("chrome://mozapps/content/extensions/extensions.xul"),
               url("chrome://browser/content/preferences/preferences.xul") {

/******** Options Window ********/

prefwindow .paneSelector.chromeclass-toolbar{
	-moz-appearance  : none !important;
        border: none !important;
        background: #0f0f14 url("data:image/png;base64,") repeat-x center !important;}

prefwindow radio[pane] {
	-moz-appearance  : none !important;
        border: none !important;
        color: White !important;
        background: transparent !important;}
prefwindow radio[pane]:not([selected="true"]) {
	-moz-appearance  : none !important;
        border: none !important;
        color: Grey !important;
        background: transparent !important;}
prefwindow radio[pane]:hover {
	-moz-appearance  : none !important;
        border: none !important;
        color: #ccc !important;
        background: transparent !important;}

/******** Add-ons Window ********/

#topStackBar .viewSelector.chromeclass-toolbar {
	-moz-appearance: none !important;
        border: none !important;
        background: #0f0f14 url("data:image/png;base64,") repeat-x center !important;}

#topStackBar radio {
	-moz-appearance: none !important;
        border: none !important;
        color: White !important;
        background: transparent !important;}
#topStackBar radio:not([selected="true"]) {
	-moz-appearance: none !important;
        border: none !important;
        color: #777 !important;
        background: transparent !important;}
#topStackBar radio:hover {
	-moz-appearance: none !important;
        border: none !important;
        color: #ccc !important;
        background: transparent !important;}

}
