css:
@-moz-document domain("beatsaver.com") {
.box {
    background-color: #35353a;
    color: #bababa;
}
.detail-content .buttons a, .detail-content .buttons button {
	color: #bababa;
}
.detail-content .box .left .metadata td:first-child, div.beatmap-result div.beatmap-content .outer div.details h2>span.uploaded {
    color: rgba(255, 255, 255, .5);
}
.detail-content .box, div.beatmap-result div.beatmap-content .outer div.details, div.beatmap-result div.beatmap-content .right .stats {
	color: #e7e7e8;
}
.detail-content .buttons a:not(:last-child), .detail-content .buttons button:not(:last-child) {
    border-right-color: rgba(255, 255, 255, .1);
}
.detail-content .buttons {
	background: #35353a;
	box-shadow: 0 2px 3px rgba(10,10,10,.1), 0 0 0 1px rgba(250, 250, 250, .102);
}
.detail-content .buttons a:active, .detail-content .buttons button:active {
    background-color: rgba(255, 255, 255, .1);
}
.detail-content .buttons a:hover, .detail-content .buttons button:hover {
    background-color: rgba(255, 255, 255, .05);
}
.tag:not(body).is-dark {
    background-color: #c7c7ca;
    color: #000;
}
div.beatmap-result div.beatmap-content {
    background-color: #35353aeb;
}
div.beatmap-result div.beatmap-content .right a:active, div.beatmap-result div.beatmap-content .right button:active {
    background-color: rgba(255, 255, 255, .1);
}
div.beatmap-result div.beatmap-content .right a:hover, div.beatmap-result div.beatmap-content .right button:hover {
    background-color: rgba(255, 255, 255, .05);
}
div.beatmap-result div.beatmap-content .right a, div.beatmap-result div.beatmap-content .right button {
    color: #bababa;
    border-color: rgba(255, 255, 255, .1);
    background-color: rgba(53, 53, 58, .4);
}
.is-checkradio[type=checkbox]+label:before, .is-checkradio[type=radio]+label:before, .is-checkradio[type=checkbox]:checked+label:before, .is-checkradio[type=radio]:checked+label:before {
	border-color: #51515e;
}
code {
	background: #404045;
}
}
