css:
@-moz-document domain("forum.hardware.fr") {
body, .backgroundbody { background-color: #151515 !important }

.cBackCouleurTab1, #mesdiscussions .message[style="background-color:#F7F7F7"] { background-color: #202020 !important }

.cBackCouleurTab2, #mesdiscussions .message[style="background-color:#DEDFDF"], .cBackTab1, .cat, .sujet, .profil, .cBackTab2, #mesdiscussions div.onglet_menu div a:hover, .onglet_menu_sel, .searchCase, .sujetCase1, .sujetCase6, .sujetCase6bis, .sujetCase9, .catCase2, .reponse, .profilCase2, .profilCase5, .profilCase4, .modoCase0, .modoCase1, .modoCase1bis, .modoCase3, .hop { background-color: #282828 !important }

#mesdiscussions .citation, #mesdiscussions .quote, #mesdiscussions .spoiler, #mesdiscussions .oldspoiler, #mesdiscussions .oldcitation, #mesdiscussions .oldquote, #mesdiscussions .fixed, #mesdiscussions .code, #mesdiscussions .ongletsel, #mesdiscussions .ongletonmouseover { background-color: #232323 !important }

mesdiscussions .caseModoGeneric, #mesdiscussions .messageModo, #mesdiscussions .messageModo *{ background-color: #4a0000 !important; color: #c5c5c5 !important }

.Topic, #mesdiscussions div.onglet_menu div a:hover, .onglet_menu_sel, .profil, .s1Topic, .s2Topic, .citation, .oldcitation, .quote, .oldquote, .message, .reponse, .Ext, .seachhelp, .searchCase, .cExt, .s1Ext, .s2Ext, .rightbutton, .titreforum, .toberead, .arbo, .menu, .code, .fixed, .nombres, .spoiler, .oldspoiler, .cLink, #mesdiscussions a.cLink, .cHeader, .cBackHeader, #mesdiscussions div.onglet_menu, .copyright, .s1, .Tableau, .s1Tableau, .s2Tableau, .cat, .sujet, .cCatTopic, #mesdiscussions a.cCatTopic, .legende, .hop { color: #c8c8c8 !important }

#mesdiscussions, #mesdiscussions td, #mesdiscussions th, #mesdiscussions div.onglet_menu div a, #mesdiscussions div.onglet_menu div span, #mesdiscussions .error, #mesdiscussions .search, #mesdiscussions .sondage, #mesdiscussions .PollWarning, #mesdiscussions table.citation, #mesdiscussions div.citation, #mesdiscussions table.quote, #mesdiscussions table.spoiler { border-color: #444 !important }

#mesdiscussions .toolbar { border-bottom-color: #444 !important }

#mesdiscussions table, #mesdiscussions div.onglet_menu div { border-right-color: #444 !important }

input[type="text"], textarea, select[name="cat"], select[name="action_reaction"], select[name="searchtype"], select[name="titre"], select[name="daterange"], select[name="resSearch"], select[name="orderSearch"], select[name="subcat"], input[id="submitreprap"], input[value="Go"], input[value="Rechercher"], input[value="Résultats suivants"], input[value="Valider"] {  background-color : #222; border-color : #333; color : #ddd;}

img[src="https://forum-images.hardware.fr/icones/message/icon1.gif"], .toolbar, img[src="https://forum-images.hardware.fr/themes_static/images_forum/1/repondre.gif"], img[src="https://forum-images.hardware.fr/themes_static/images_forum/1/newsujet.gif"] { filter:brightness(0.8) !important }

img[src="https://forum-images.hardware.fr/themes_static/images_forum/1/tline.gif"], img[src="https://forum-images.hardware.fr/themes_static/images_forum/1/tline3.gif"], img[src="https://forum-images.hardware.fr/themes_static/images_forum/1/tline4.gif"] { filter:invert(0.9) !important }

#mesdiscussions .beforongletsel, #mesdiscussions .afterongletsel, #mesdiscussions .ongletsel, #mesdiscussions .ongletonmouseover { background-image: none !important }


}
