css:
@-moz-document domain("mycourses2.mcgill.ca")

{

/*
    Name:       MyCourses Dark Mode (Beta)
    Version:    0.1
    Author:     David Holcer (http://github.com/davidholcer)
*/

html {
    --dark_color: #232323;
    /*--dark_color:#142020;*/
    /*--dark_color: #303437;*/
    /*--less_dark_color:#557776;*/
    /*--less_dark_color_transparent:#3c5352c9;*/
    --less_dark_color: #393939;
    /*--less_dark_color: #373D3F;*/
    --less_dark_color_transparent: #393939ab;
    --primary_text_color: #fdfaf0;
    /*--primary_text_color: #1abbb9;*/
    --primary_text_color_a: #fafdeb26;
    --secondary_text_color: #fafdeb;
    --course-image-card-height: 200px !important;
    /*--secondary_text_color: #a6a6a6;*/
}

html,
body,
.d2l-navigation-s,
.d2l-navigation-s-button-highlight,
.d2l-dropdown-opener,
.dco_c
  {
    background:  var(--dark_color) !important;
    color: var(--primary_text_color);
  }

/*
don't remember why i had this
aside.v-navigation-drawer.v-navigation-drawer--clipped.v-navigation-drawer--fixed.v-navigation-drawer--open.theme--light {
    background-color: #db3636 !important;
}
*/
/*pushes notification tab to bottom*/
.homepage-col-6 {
    width: 100%;
}

.homepage-row {
    clear: both;
    width: 100%;
    float: left;
    margin-left: 0%;
    margin-right: 0;
}

/*Course progress sidebar */
.daylight .d2l-datalist-nesteditem-accordion,
.daylight .d2l-datalist-nesteditem-accordion > .d2l-datalist-item-content.d2l-datalist-item-content-first {
    background: #393939;
}

.daylight .d2l-datalist > .d2l-datalist-nesteditem-accordion .d2l-datalist > .d2l-datalist-nesteditem-accordion.d2l-le-DataListItem-Selected > .d2l-datalist-item-content.d2l-datalist-item-content-first {
    background: #393939;
}

.d2l_1_43_932 {
    background-color: #393939;
}

.d2l-datalist-style1 > .d2l-datalist {
    padding-bottom: 0rem;
}

.daylight .d2l-datalist-nesteditem-accordion.d2l-datalist-nesteditem-accordion-open,
.daylight .d2l-datalist-nesteditem-accordion.d2l-datalist-nesteditem-accordion-open > .d2l-datalist-item-content.d2l-datalist-item-content-first {
    background: #d694ae;
}

/*grades section*/
.d2l-table > * > tr,
d2l-tr {
    background-color: #23395d;
    color: var(--primary_text_color);
}

.daylight .d_ggl1 {
    background-color: #95395c;
    /*color: var(--dark_color); */
}

.d2l-table {
    background-color: lavender;
    /*#darkslateblue;*/
    /*color: var(--dark_color); */
}

.d2l-dropdown-content-width {
    background-color: var(--dark_color);
}

.d2l-table > tbody,
d2l-tbody {
    background-color: var(--less_dark_color_transparent);
}

#d2l_body,
.d2l-body {
    background-color: var(--dark_color);
}

.d2l-navigation-header-container {
    height: 80px;
}

.d2l-tiles-container {
    background-color: var(--dark_color);
}

.d2l-navigation-gutters {
    background-color: var(--dark_color);
}

.d2l-navigation-s {
    background-color: var(--dark_color);
}

.d2l-branding-navigation-background-color {
    background-color: var(--dark_color);
}

.daylight .d2l-collapsepane .d2l-collapsepane-header {
    background: #e6e6fa8f;
}

.d2l-navigation-s-header-logo-area a.d2l-navigation-s-link,
.d2l-navigation-s-header-logo-area a.d2l-navigation-s-link:link,
.d2l-navigation-s-header-logo-area a.d2l-navigation-s-link:visited {
    color: var(--primary_text_color);
}

.d2l-branding-navigation-dark-foreground-color a.d2l-navigation-s-link,
.d2l-branding-navigation-dark-foreground-color a.d2l-navigation-s-link:link,
.d2l-branding-navigation-dark-foreground-color a.d2l-navigation-s-link:visited,
.d2l-branding-navigation-dark-foreground-color div.d2l-navigation-s-item > span {
    color: var(--primary_text_color);
}

.d2l-typography {
    background-color: var(--dark_color);
}

element.style {
    background-color: var(--dark_color)
}

.homepage-col-8 {
    width: 100%;
}

.d2l-branding-navigation-dark-foreground-color .d2l-navigation-s-group .d2l-navigation-s-group-text,
.d2l-branding-navigation-dark-foreground-color .d2l-navigation-s-group .d2l-navigation-s-group-wrapper {
    color: var(--primary_text_color);
}

.homepage-container * {
    background-color: var(--less_dark_color_transparent);
    border-radius: 10px;
}

.course-text {
    color: var(--primary_text_color) !important;
}

.d2l-typography .d2l-htmlblock p {
    color: var(--secondary_text_color);
}

h1.d2l-heading-1.module-header {
    color: var(--primary_text_color);
}

#app #root-wrapper .module-display .module-inner-container .module-description-body {
    color: var(--primary_text_color);
}

.d2l-widget-header .d2l-heading {
    color: var(--primary_text_color);
}

.d2l-datalist-item-content > div {
    color: var(--primary_text_color);
}

/*fails do to shadow root
.grid-container {
    color: var(--primary_text_color);
}
.d2l-floating-buttons-container {
    background-color: rgba(57, 57, 57, .96);
    box-shadow: rgb(0 0 0 / 0.4) 0px -2px 4px;
}
*/
.d2l-max-width {
    color: var(--primary_text_color);
    background-color: var(--dark_color);
}

.d2l-typography .d2l-heading-1,
.d2l-typography .d2l-heading-2,
.d2l-typography .d2l-heading-3,
.d2l-typography .d2l-heading-4,
.d2l-typography .vui-heading-1,
.d2l-typography .vui-heading-2,
.d2l-typography .vui-heading-3,
.d2l-typography .vui-heading-4 {
    color: var(--primary_text_color);
}


/* Header bar rainbow thingy */
@keyframes colorchanger {
    0% {
        --d2l-branding-primary-color: #45A3E5;
    }
    2% {
        --d2l-branding-primary-color: #48A6D5;
    }
