    }
    22% {
        --d2l-branding-primary-color: #66bf39;
    }
    24% {
        --d2l-branding-primary-color: #72B735;
    }
    26% {
        --d2l-branding-primary-color: #7EAF31;
    }
    28% {
        --d2l-branding-primary-color: #8AA72E;
    }
    30% {
        --d2l-branding-primary-color: #969F2A;
    }
    32% {
        --d2l-branding-primary-color: #A29726;
    }
    34% {
        --d2l-branding-primary-color: #AF8F22;
    }
    36% {
        --d2l-branding-primary-color: #BB871E;
    }
    38% {
        --d2l-branding-primary-color: #C77F1A;
    }
    40% {
        --d2l-branding-primary-color: #D37717;
    }
    42% {
        --d2l-branding-primary-color: #DF6F13;
    }
    44% {
        --d2l-branding-primary-color: #EB670F;
    }
    46% {
        --d2l-branding-primary-color: #ED6215;
    }
    48% {
        --d2l-branding-primary-color: #EF5E1C;
    }
    50% {
        --d2l-branding-primary-color: #F05922;
    }
    52% {
        --d2l-branding-primary-color: #F25428;
    }
    54% {
        --d2l-branding-primary-color: #F44F2F;
    }
    56% {
        --d2l-branding-primary-color: #F64B35;
    }
    58% {
        --d2l-branding-primary-color: #F8463C;
    }
    60% {
        --d2l-branding-primary-color: #FA4142;
    }
    62% {
        --d2l-branding-primary-color: #FB3C48;
    }
    64% {
        --d2l-branding-primary-color: #FD384F;
    }
    66% {
        --d2l-branding-primary-color: #FF3355;
    }
    68% {
