    }
    80% {
        --d2l-branding-primary-color: #B24398;
    }
    82% {
        --d2l-branding-primary-color: #A745A2;
    }
    84% {
        --d2l-branding-primary-color: #9C47AC;
    }
    86% {
        --d2l-branding-primary-color: #914AB5;
    }
    88% {
        --d2l-branding-primary-color: #864CBF;
    }
    90% {
        --d2l-branding-primary-color: #7B5BC5;
    }
    92% {
        --d2l-branding-primary-color: #7069CC;
    }
    94% {
        --d2l-branding-primary-color: #6678D2;
    }
    96% {
        --d2l-branding-primary-color: #5F82D6;
    }
    98% {
        --d2l-branding-primary-color: #578BDB;
    }
    100% {
        --d2l-branding-primary-color: #5095DF;
    }
}


html {
    -webkit-animation: colorchanger 20s infinite;
    animation: colorchanger 20s infinite;
    -webkit-animation-direction: alternate;
    animation-direction: alternate;
    --d2l-branding-primary-color: #804167d1;
}


/*START assignment submit*/
h1.d2l-heading.vui-heading-3 {
    color: var(--primary_text_color);
}

.ddial_o2 {
    background-color: var(--less_dark_color);
}

span.d2l-textblock {
    color: var(--secondary_text_color);
}

div#MFI_control {
    background-color: var(--dark_color);
}

.d2l-fileinput-drop-msg {
    color: var(--secondary_text_color);
}

.d2l-fileinput-maxsize-msg {
    color: var(--secondary_text_color);
}

/*border ??
.d2l-dialog-mvc .d2l-dialog-inner, .ddial_o2 {
}
*/
/*END assignment submit*/
.cclangsel {
    background-color: var(--dark_color);
}

.theme--light.v-input:not(.v-input--is-disabled) input,
.theme--light.v-input:not(.v-input--is-disabled) textarea {
    color: var(--primary_text_color);
}

/*sidebar*/
.d2l-twopanelselector-side-bg.d2l-twopanelselector-side-sep,
.d2l-twopanelselector-side.d2l-twopanelselector-side-sep {
    border-right: 1px solid #e3e9f1;
    background: linear-gradient(90deg, var(--primary_text_color_a) 0, var(--dark_color) 320px);
}
.daylight #ContentModuleTree a.d2l-le-TreeAccordionItem-anchor,
.daylight #ContentModuleTree a.d2l-le-TreeAccordionItem-anchor:link,
.daylight #ContentModuleTree a.d2l-le-TreeAccordionItem-anchor:visited,
.daylight #ContentModuleTree a.d2l-le-TreeAccordionItem-anchor:hover,
.daylight #ContentModuleTree a.d2l-le-TreeAccordionItem-anchor:focus {
    color: var(--secondary_text_color);
}

.daylight .d2l-le-disc-post {
    background-color: var(--dark_color);
}

.homepage-container * {
    color: var(--dark_color);
}
}
