css:
@-moz-document domain("steamworkshopdownloader.io") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 2px;
             background-color: #020202;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 8px;
             background-color: #020202;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 2px;
             -webkit-box-shadow: inset 0 0 6px rgba(255, 255, 255, .14);
             background-color: #010140e6
        }

.glass.card {
    background-color: #0a0c0fbf;
}
.form-control {
    color: #d6dadd !important;
    background-color: rgba(0, 0, 0, .2) !important;
    border-color: #525960;
}
body {
	background-color: #020202;
	color: #fff;
}
.card {
    background-color: #0a0c0fd9;
    border-color: rgba(255, 255, 255, .125);
}
.input-group-text {
    color: #d6dadd;
    background-color: #4d525733;
    border-color: #525960;
}
.bg-light {
    background-color: #3b3d40 !important;
}
a.bg-light:hover, a.bg-light:focus, button.bg-light:hover, button.bg-light:focus {
    background-color: #5e656c !important;
}
.text-muted {
    color: #90989f !important;
}
.glass.alert-warning {
    background-color: #0f0d07b3;
}
.alert-warning {
    color: #bd9200;
    background-color: #0f0d07b3;
    border-color: #0d0b03;
}
.img-thumbnail {
	background-color: #000;
	border-color: #42464a;
}
* {
	filter: inherit !important;
}
.list-group-item {
    background-color: rgba(0, 0, 0, .4);
    border-color: rgba(255, 255, 255, .125);
}
.list-group-item-action {
    color: #d6dadd;
}
.list-group-item-action:active {
    color: #fff;
    background-color: #4d525766;
}
.list-group-item-action:hover, .list-group-item-action:focus {
    color: #d6dadd;
    background-color: #3b3f4266;
}
.btn-light:not(:disabled):not(.disabled):active, .btn-light:not(:disabled):not(.disabled).active, .show > .btn-light.dropdown-toggle {
    color: #fff;
    background-color: #5e646a;
    border-color: #565d64;
}
.btn-light:hover {
    color: #fff;
    background-color: #45494d;
    border-color: #5e646a;
}
.btn-light, .btn-light:focus, .btn-light.focus {
	color: #fff;
}
.LatestUpdates_showAll__3MwOF button {
    border-color: rgba(255, 255, 255, .1);
}
.bg-white {
	background: #000 !important;
}
.LatestUpdates_latestUpdatesCollapsed__2MAwf:after {
    background-image: linear-gradient(180deg,hsla(0,0%,93.7%,0),#08081bbf 90%);
}
.custom-control-label::before {
    background-color: #000;
    border-color: #737e88;
}
.glass:before {
    background-color: #080846bf;
}
[target="_blank noopener noreferrer"] > img {
	object-fit: contain;
	width: auto;
}
[class="downloadItemImage float-left do-not-darkmode"] {
	object-fit: contain;
	width: auto;
}
}
