@-moz-document
 url-prefix(http://) ,
 url-prefix(https://)
{

a { 
   text-decoration : underline !important
}

a:visited {
    /* Dire on dark backgrounds, sadly: color: #551A8B !important; */

    /* Doesn't work (yet): text-line-style: dotted; */
