css:
@-moz-document domain("forum.cockos.com") {
/* Remove the banner from top with links to Reaper resources, only show forum */
div.black {
	display: none;
}

/* Set main forum container width to 70% of screen width */
div.strip {
	width: 60rem;
	padding-top: 1em;
	padding-bottom: 1em;
}

/* Give posts and the forum sections a more modern look. Color background mintcream, add 3D box shadow, round the borders */
div.page {
	padding: 20px;
	background: mintcream;
	border-radius: 10px;
	box-shadow: 0px 8px 17px 2px rgb(0 0 0 / 14%), 0px 3px 14px 2px rgb(0 0 0 / 12%), 0px 5px 5px -3px rgb(0 0 0 / 20%);
	margin: 20px;
	width: auto !important;
}

div#posts div.page {
	padding-top: 15px;
	padding-bottom: 15px;
	padding-left: 20px;
	padding-right: 20px;
}

td.tcat {
	background: mintcream;
}

/* Remove borders between posts */
table {
	border-collapse: collapse;
}

td.alt1, td.alt1Active, td.alt2, td.alt2Active {
	border-bottom: 1px solid silver;
}
}
