css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("reader.aol.com") {
/* 去除mac回弹 
html, body {overflow:hidden !important}*/
/* remove ads */
.reader-ad-container {display:none}
#reader-container { margin-right: 0px!important}
.feed-header { right: 0px!important}

/* remove header */
.header{display:none !important}
#reader-container { margin-top: 0px!important}
.feed-header, 
.nav {top: 0px!important}
.article-list-pane {top: 50px!important}

/* list */
.article-item-list {padding: 10px 4px !important;}
.article-item-list .title {font-size:14px !important}

/* content */
.is-desktop .article-pane-full{float:none !important;margin-left:430px !important; padding-bottom:300px !important}
.article-item-view .article-content {width:auto !important;max-width:700px !important}
.article-body img{max-width:1150px !important}
.article-item-full .article-body img{max-width:700px !important}
}
