css:
@-moz-document domain("github.com") {
    [data-color-mode="dark"] * {
        --color-bg-canvas: hsl(218, 14%, 11%);
        --color-bg-canvas-inset: hsl(218, 14%, 9%);
        --color-bg-primary: hsl(218, 14%, 11%);
        --color-bg-secondary: hsl(214, 14%, 10%);
        --color-header-bg: hsl(210, 15%, 8%);
        --color-box-header-blue-bg: hsl(218, 14%, 11%);
        --color-text-primary: hsl(207, 18%, 88%);
        --color-bg-tertiary: hsl(220, 13%, 14%);
        --color-text-link: hsl(206, 100%, 67%);
        --color-bg-info: rgba(56, 139, 253, 0.02);
        --color-underlinenav-text-active: hsl(0, 0%, 92%);
        --color-btn-bg: hsl(215, 20%, 17%);
        --color-bg-overlay: hsl(219, 18%, 15%);
        --color-calendar-graph-day-bg: hsl(218, 14%, 13%);
        --color-input-bg: hsl(218, 19%, 10%);
        --color-calendar-graph-day-border: hsl(218, 14%, 14.5%);
        --color-markdown-code-bg: rgba(240,246,252,0.10);
    }
}
