      color: #ff00ff !important;
      font-size: 1.25em;
      text-shadow: 0 0 1px #fff, 0 0 16px #fff;
  }
  .cm-variable {
      color: cyan !important;
  }
  .cm-variable-2 {
      text-shadow: 0 0 1px #fff, 0 0 16px #fff;
