css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix( http://www.toodledo.com/views/), url-prefix( https://www.toodledo.com/views/) {

 body, textarea, input, select, option { font: 12px/14px 'Calibri' !important;  font-weight: bold !important}

#viewby { color: #333 !important; font-size: 8px !important; margin-bottom: 3px !important;
}

 #viewby::before       { margin-right: 20px !important; content: "[m|o|c|d|g|p|h|e]" !important;
}


/* Change context appearance (i=#) in context view */

SPAN[old="334344"]{
font-weight: bold !important;
font-size: 10pt !important;
color: white !important;
background-color: black !important;
padding: 0px 28% 0px 28% !important;

}
/* ne */
SPAN[old="323319"]{
color: green !important;
}
/* cm */
SPAN[old="323320"]{
color: red !important;
}
/* out */
SPAN[old="323244"]{
color: orange !important;
}
/* comm */
SPAN[old="323323"]{
color: blue !important;
}


#filtertask, #sorttask, #sharetask, #searchtask {
border: 0px solid #fff !important;
margin-top: 0 !important;
}

/* Column Headers */

#colhead, #colheadrow {
border-bottom:1px solid #ccdee5 !important; height: 14px !important;
padding: 4px 0 !important;
width: 100% !important;
}

#colhead { font-size: 8pt !important; 
}

a {
color: #5797B0 !important;
text-decoration: none !important;
}

a:hover {
color: #3d6b7d !important;
text-decoration: none !important;
}

body {
background: #1A2022 !important;
}

.bottom {
border-top: 1px solid #333 !important;
margin-top: 10px !important;
padding-top: 10px !important;
}

#brg {color: #333 !important;
padding: 10px !important;
background: #d7f7d3 !important;
}

.connectbox { color: #333 !important;
}

#forumwrap {
width: 99% !important;
}

h1, h2 { color: #999 !important;
}

#head { background-color:#e3f7ff !important;
border: solid 1px #333 !important; font-size: 0.9em !important;
}


#logo {
display: none !important;
}

.row {
border-bottom: 1px solid #eee !important;
padding:3px 0 2px !important;
}

.row:hover {
background-color: #fafaf1 !important;
}

.sep { background: #f7fdff !important;
font-style: italic !important;
}

/* Subtasks */

.subtasks .row {
border-top: 1px solid #eee !important;
border-bottom: none !important;
}

.subtasks {margin-left: 21px !important;
font-size: 9pt !important;
}

.subtasks .row:hover {
background-color: #faf6e1 !important;
}

.moresubs {
margin-left: -21px !important;
margin-top: -16px !important;
padding-bottom: 6px !important;
border-bottom:2px dotted #AAAAAA;
}

.moresubs > A:last-child {display:none !important}

.right.mrsbs {
margin-top: 9px !important;
margin-right: 220px !important;
font-size: 0.2em !important;
}

/* Tabs */

.tab { 
padding: 4px !important;
background: #f7fdff !important;
margin: 0 !important;
margin-right: -1px !important;
height: 14px !important;
border: solid 1px #ebf0f2 !important;
border-bottom: none !important;
font-weight: normal !important;
padding-left: 7px !important;
padding-right: 7px !important;
font-style: italic !important;
}

.tabon { background: #e3f7ff !important;
height: 15px !important;
padding: 4px !important;
padding-left: 7px !important;
padding-right: 7px !important;
margin: 0 !important;
border: solid 1px #ccdee5 !important;
border-bottom: none !important;
}

.tabon a {
color: #333 !important;
}


#tabs { height: 24px !important; font-size: 12px !important; margin-left: 3px !important;
}

#tabs > DIV:first-child > ::before  {font-size: 7px !important; margin-right: 3px !important;color: black !important; content: "1:" !important; }
#tabs > DIV:first-child + DIV> ::before  {font-size: 7px !important; margin-right: 3px !important;color: black !important; content: "2:" !important;}
#tabs > DIV:first-child +DIV+DIV> ::before  {font-size: 7px !important; margin-right: 3px !important;color: black !important; content: "3:" !important;}
#tabs > DIV:first-child +DIV+DIV+DIV> ::before  {font-size: 7px !important; margin-right: 3px !important;color: black !important; content: "4: " !important;}
#tabs > DIV:first-child +DIV+DIV+DIV+DIV> ::before  {font-size: 7px !important; margin-right: 3px !important;color: black !important; content: "5: " !important;}
#tabs > DIV:first-child +DIV+DIV+DIV+DIV+DIV> ::before  {font-size: 7px !important; margin-right: 3px !important;color: black !important; content: "6: " !important;}
#tabs > DIV:first-child +DIV+DIV+DIV+DIV+DIV+DIV> ::before  {font-size: 7px !important; margin-right: 3px !important;color: black !important; content: "7:" !important;}
#tabs > DIV:first-child +DIV+DIV+DIV+DIV+DIV+DIV+DIV> ::before  {font-size: 7px !important; margin-right: 3px !important;color: black !important; content: "8:" !important;}
#tabs > DIV:first-child +DIV+DIV+DIV+DIV+DIV+DIV+DIV+DIV> ::before  {font-size: 7px !important; margin-right: 3px !important;color: black !important; content: "9:" !important;}

#toolbar {
background-color: #fff !important;
padding: 5px 5px !important;
border: none !important;
border-bottom: dotted 1px #ddd !important;
color: #333 !important;
margin-top: 0 !important;
}

/* Left Column */

#logo,.tl,#tocc, #toco {
display:none !important;
}

#toc {
padding: 0 0px 0px 0px !important;
margin: 0px 0px 0px 2px !important;
font-size: 0.01% !important;
line-height: 6px !important;
}


/* 'ADD TASK' BUTTON */

#action_addtask {
padding: 6px 12px 0px 24px !important;
height: 19px !important;
background: url(http://www.nonimage.com/stylish/toodledo/button2.png) no-repeat left 2px !important;
margin: -4px 1px 0 0 !important;
text-align: left !important;
color: #000 !important;
font-size: 0.9em !important;
}

#action_addtask img {
display: none !important;
}

#action_cancelAdd {
margin: 1px 12px 0 0 !important;
color: #000 !important;
font-size: 1em !important;
color: #d57800 !important;
}

/* 'ADD TASK' FORM */

#addtask  {
background-color: #fff !important;
border: 3px solid #333 !important;
margin: 0 10px 20px 0px !important;
padding: 10px !important;
color: #333 !important;
}

#addtask a {
color: #333 !important;
text-decoration: none !important;
}

#trg, #tlg, #brg, #blg {
background-image: none !important;
}

.field {
margin-bottom: 10px !important;
}

 #sp2 {font-size: 1% !important;
}
 #sp4 {font-size: 1% !important;
}
 #sp16 {font-size: 1% !important;
}
 #sp6 { font-size: 1% !important;
}
 .bottom {font-size: 1% !important;
}
 #action_searchtask::after       { margin-left: 2px !important; content: "[f]" !important;
}
 #action_addtask::after       { margin-left: 2px !important; color: black !important;
content: "[n]" !important;
}

#main {
display: inline-table !important;
margin: 0px 0px 0px -129px !important;
padding: 5px 4px 4px 4px !important;
width: 95% !important;
background:#FFFFFF none repeat scroll 0 0 !important;
border:3px solid #EEEEEE !important;
-moz-border-radius: 6px;
}

/* FLUID WIDTHS (EXPERIMENTAL) */
#tasks {
width: 100% !important;
margin: 0 !important;
font-size: 10pt !important;
}

.ch, .chd, .st {
width: 20px !important;
text-align: left !important;
}

.col0, .col1, .col2, .col3, .col4, .col5, .col6, .col7, .col8, .col10, .col11, .col12, .col13, .col14, .col15, .col16, .col64, .col128, .col256, .col512, .col1024, .col2048, .col4096, .col8192, .col32768, .col65536, .col97, .col98, .col99 {
margin: 0px 1px !important;
}

/* Title */
.col0 {
width: 38% !important;
min-width:50px !important;
padding-left: 20px !important;
}

/* Due Date */

.date1 {
background-color: #EEE !important;
color: #f73b42 !important;
padding-right: 100% !important;
}

.date2 {
color: #f73b42 !important;
padding-right: 100% !important;
}

.date3{
background-color: #f73b42 !important;
color: #fff !important;
padding-right: 100% !important;
}

.col2 {
width:  9% !important;
min-width: 10px !important;
font-weight: bold !important;
}

/* Repeat */
.col4 {
width:  6% !important;
min-width: 10px !important;
font-style: italic !important;
text-align: left !important;

}

/* Tag */


.col128 {
text-align: right !important;
width:  6% !important;
min-width: 20px !important;
}

/* Context */
.col512 {
width:  10% !important;
min-width: 20px !important;
padding-left: 5px !important;
}

/* Start Date */
.col65536 {
width:  7% !important;
min-width: 10px !important;
text-align: left !important;
}

/* Date Complete */
 .col98 {
min-width:  6px !important;
max-width: 6px !important;
text-align: left !important;
 display:none !important
}

/* goal */
.col1024::before  {content: "*" !important;padding:3px !important}
.col1024 {
min-width:  12px !important;
max-width: 12px !important;
text-align: left !important;
}

.col1024:hover {
text-align: left !important;
width:  10% !important;
min-width: 70px !important;
overflow: hidden !important;
}


/* Folder */
.col1 {
width:  9% !important;
max-width: 50px !important;
text-align: left !important;
font-size: 8pt !important;
}

/* Priority */

.pri0 {
font-size: 8pt !important;
text-align: left !important;
}

.pri1 {
background-color: #f8f3c5 !important;
color: #111 !important;
font-size: 8pt !important;
padding-right: 100% !important;

}

.pri2 {
background-color: #9ed5f7 !important;
color: #111 !important;
font-size: 8pt !important;
padding-right: 100% !important;
}

.pri3 {
background-color: #9ef7b3 !important;
color: #111 !important;
font-size: 8pt !important;
padding-right: 100% !important;
}

.col16 {
min-width: 15px !important;
max-width: 65px !important;
width: 5% !important;
text-align: center !important;
}

/* Length */
.col8 { width: 4% !important;
max-width: 45px !important;
}

/* Delete and Edit Button */
.cols .del { margin: 0px 0px 0px 5px !important; }
.cols .note, .cols .ednote  { margin: 0px 0px 0px 0px !important;}

/* Dimmed Text */

.dim  {color: #DDD !important; font-size: 8pt !important; font-style: normal !important;
}



/* Others */
.col2048, .col256 {
width: 5px !important;
min-width: 5px !important;
}

}
