css:
@-moz-document domain("web.telegram.org") {
body {
    background: #0e1621 !important;
}
.im_history_col .nano > .nano-pane > .nano-slider, .contacts_modal_col .nano > .nano-pane > .nano-slider, .sessions_modal_col .nano > .nano-pane > .nano-slider, .stickerset_modal_col .nano > .nano-pane > .nano-slider, .im_dialogs_modal_col .nano > .nano-pane > .nano-slider {
    background: rgba(255, 255, 255, 0.5);
}

/*fondo*/
.page_wrap{
 background: #0e1621 !important;
}
/*header*/
.tg_head_split {
    background: #212d3b !important;
}
.tg_head_logo_dropdown .dropdown-toggle {
    color: #fdfdfd !important;
}
.tg_head_peer_title {
	color: #fdfdfd !important;
}
.tg_head_peer_media_dropdown .dropdown-toggle .icon-bar {
    background-color: #fdfdfd !important;
}
.icon-hamburger-wrap .icon-bar {
    background: #fdfdfd !important;
}
/*hover*/
.dropdown.open .tg_head_btn, .tg_head_btn:hover {
    color: #fdfdfd !important;
    background: #2b5278 !important;
}
/*el menu xd*/
.dropdown-menu {
    background-color: #17212b !important;
    border: 1px solid #17212b !important;
}
.tg_head_logo_dropdown .dropdown-menu > li > a:hover {
    background: #232e3c !important;
}
.tg_head_logo_dropdown .dropdown-menu > li > a {
    color: #e9e9ce !important; 
}
.tg_head_peer_dropdown .dropdown-menu > li > a {
    color: #f5f5f5 !important;
}
.tg_head_peer_dropdown .dropdown-menu > li > a:hover {
    background: #232e3c !important;
}
.dropdown-menu .divider {
    background-color: #f5f5f5 !important;
}
.tg_head_peer_dropdown .dropdown-menu {
    max-width: 100%;
    border-radius: 0;
    padding: 9px 0;
    float: none;
    right: 0;
    left: auto;
    margin: -1px 0 0 0;
    border: 1px solid #17212b !important;;
    box-shadow: 0px 1px 3px 0px rgba(255,255,255,0.3) !important;
}
/*-------------------------------------------------*/
/*bots*/
.im_bot_intro_message_wrap {
    color: #fff !important;
}
/*cuando le haces click a un user*/
.reply_markup_button:focus, .reply_markup_button:hover {
    color: #fff !important;
    background: rgba(255,255,255,0.2) !important;
}
.reply_markup_button, .reply_markup_button{
      color: #fff !important;
    background: rgba(255,255,255,0.1) !important;
}
.md_modal_section_param_name{
color: #fff !important;

}
.md_modal_section_param_value{
color: #fff !important;
}
.md_modal_body{
background: #17212b !important;
}
/*la parte de los chats*/
.im_message_photo_caption{
color: #fff !important;
}
.im_message_webpage_description{
color: #fff !important;

}
a {
    color: #83caff !important;
}
.im_dialogs_col_wrap {
    border-right: 2px solid #212122!important;
}
.im_page_wrap {
    background: #17212b !important;
    -webkit-box-shadow: 0px 1px 0 #17212b !important;
    -moz-box-shadow: 0px 1px 0 #17212b !important;
    box-shadow: 0px 1px 0 #17212b !important;
    border-left: 1px solid #17212b !important;
    border-right: 1px solid #17212b !important;
    border-bottom: 1px solid #17212b !important;
}
.im_dialogs_col .nano > .nano-pane {
    background: rgba(255, 255, 255, 0.2);
	width: 12px;
}
.im_dialogs_col .nano > .nano-pane > .nano-slider {
    background: #787f85 !important;
    margin: 0!important;
}

/*hover de los chats*/
.im_dialogs_scrollable_wrap a.im_dialog:hover, .im_dialogs_scrollable_wrap a.im_dialog_selected {
    background: #202b36 !important;
}

/*colo de los chats*/
.im_dialog_peer {
    color: #fff !important;
}
/*chats normales creo*/
a.im_dialog .im_short_message_text {
    color: #7f91a4!important;
}
.im_dialogs_scrollable_wrap .active a.im_dialog {
    background-color: #2b5278 !important;
}
a.im_dialog .im_dialog_chat_from_wrap, a.im_dialog .im_short_message_media, a.im_dialog .im_short_message_service {
    color: #73b9f5 !important;
}
a.im_dialog:hover .im_short_message_text, a.im_dialog_selected .im_short_message_text {
    color: #7f91a4 !important;
}
.active a.im_dialog .im_dialog_chat_from_wrap, .active a.im_dialog .im_dialog_draft_from, .active a.im_dialog .im_dialog_message, .active a.im_dialog .im_short_message_media, .active a.im_dialog .im_short_message_service, .active a.im_dialog .im_short_message_text {
    color: #fff!important;
}
/*barra de busqueda*/
.im_dialogs_search_field {
    border: 1px solid #242f3d !important;
    background-color: #242f3d !important;
	color: #f5f5f5 !important;
}
.im_dialogs_scrollable_wrap a.im_dialog_searchpeer, .im_dialogs_scrollable_wrap a.im_dialog_searchpeer:hover, .im_dialogs_scrollable_wrap a.im_dialog_searchpeer_selected {
    background: #202b36 !important;
}

/*-------ahora la parte el caht donde se escribe*/

/*mensje sellecionado*/
.im_message_selected .im_message_outer_wrap {
    background: #25303e !important;
}
.im_history_select_active .im_message_outer_wrap:hover {
    background: #0e1621 !important;
}

/*boton de cancelar*/
.btn-md:hover {
    background: #1d2a39 !important;
}
/*los otrsos botones*/
.btn-primary {
    background-color: #3476ab!important ; 
}


/*marco de las fotos*/
.modal-content {
    background-color: rgba(0,0,0,0)!important ;
}

/*porvemos xd*/
.im_history_wrap{
background-color: #0e1621!important ; 
}

/*querido usuario, si quieres puede poner el link de una foto aquí :3
.im_history_wrap{
background: url("https://www.mediafire.com/convkey/599f/df0q5ehwgfb4pdh9g.jpg")!important ; 
	
}
*/
/*menu de  emotes*/
.composer_emoji_tooltip {
    border: 1px solid #17212b !important;
    
    background: #17212b !important;
}
.composer_emoji_tooltip_category i {
    filter: invert(1)!important;
}
.active > i{
    filter: inherit!important ;
}

/*stickers*/
.composer_stickerset_title{
color: rgb(200,200,200) !important;
}

/*ña flechita de abajo*/
.icon-tooltip-tail {
    background:  #17212b !important;
    border: 1px solid  #17212b !important;
}
/*hover de los eotes*/
a.composer_emoji_btn:hover {
    background-color: #232e3c !important;
}
.composer_sticker_btn:hover {
    background-color: #232e3c !important;
}
/*cuando se muestran los emojis*/
.composer_dropdown_wrap {
     background:  #17212b !important;
}
.composer_dropdown li a:hover, .composer_dropdown li.composer_autocomplete_option_active a {
    background-color: #232e3c !important;
}
/*textarea*/
.composer_rich_textarea{
color: #fff;
}
/*microfono*/
.im_record_bg {
    background: #0e1621 !important;
}

/*mensaje emergente*/
.confirm_modal_wrap{
background: #17212b !important;
	color: #f5f5f5 !important;
}

.im_message_text{
color: #f5f5f5 !important;
}
}
