css:
@-moz-document domain("fanficfanfic.tumblr.com") {
body header .description {
    display: none;
}

body header #portrait {
    display: none;
}

#logo {
    display: none;
}

body header #nav li a {
    display: none;
}

body header.sidebar {
    width: auto;
}

#search {
    display: none;
}

.social-icons {
    display: none;
}

body footer {
    display: none !important;
}

.hidden-xs {
    display: none !important;
}

.col-md-4 {
    width: 10%;
}

.col-md-8 {
    width: 90%;
}

.container {
    max-width: none !important;
}
}
