html {background:#ccc!important}

[id^="overhead"] {background:#333!important}
[id^="overhead"] a {color:#fff!important}
[id^="overhead"] [class="oh-darker"] {color:#fff!important}
[id="oh-menu-deviant"] a {color:#ff0042!important;font-weight:bold!important}
[id="oh-menu-deviant"] [class="oh-menu"] a {color:#fff!important}
.oh-menu, #overhead-collect #lub, .popup2-click-friends {background:#333!important}
[id^="more7"] > b {color:#ff0042!important}
[id="more7-main"] a:hover, [id="more7-hidden"] a:hover, [class="oh-menu"] a:hover, [class="oh-menu iconset-messages"] a:hover {background:#ff0042!important}
[class="readout"] {background:#333!important}
[class="readout"] {color:#e3e3e3!important}
[class="readout"] dd[class="f"] {background:#555!important}
[class="readout"] dd[class="f"] a {color:#fff!important}
[class="readout"] dd[class="f a"] {background:#333!important}
[class="readout"] dd[class="f a"] a {color:#fff!important}
[class="readout"] span {color:#e3e3e3!important}
[class="controls"] {color:#ff0042!important;font-weight:bold!important}
[class="controls"] small {color:#fff!important}
div[class="collection-head"] [class="h1"] {color:#fff!important}
[class="collect-tagline"] {color:#ff0042!important;font-weight:bold!important}
[class="stackzone"] [class="label"] {color:#fff!important}
[class="stackzone maybedrop"] {background:#ff0042!important;font-weight:bold!important}

[class="f btd-browse"] {background:none!important}
table[class="browse2 f full"] {background:#ccc!important}
table[class="browse2 f full"] td[class="f"] {background:#bbb!important}
table[class="browse2 f full"] td[class="f"] a {color:#333!important}
[id="facetMenu-Category"] a:hover, [id="facetMenu-Browse"] a:hover {background:#ccc!important}
[id="mcstatus"] {background:none!important}
[id="browse2"] > [id="browseNavbar2"] {background:none!important}
[id="heyboxContents"] {color:#111!important}
[id="heyboxContents"] strong {color:#333!important}
[class="tt-w"] [class="t"] {color:#ff0040!important}
[class="tt-w"] small {color:#111!important}
[class="tt-w"] small a {color:#111!important}

[class="catbar"] {background:url('http://bit.ly/bPrgqW') top!important;border-color:#333!important}
[class="blues-bar"] {background:#ccc!important;border-color:#aaa!important}
[class="blues-bar-ctrl"] {background:url('http://bit.ly/bCi8FG') bottom !important;border-color:#aaa !important}

[id="depths"] {background:#555!important;color:#ddd!important}
[id="depths"] a {color:#ddd!important}
[id="depths"] [class^="footer"], [id="depths"] [class^="dev"], [id="depths"] h2 {color:#ddd!important}

div[id="gmi-Gruser"] [class="bubbleview"] {background:#ccc!important}

div[id="dv7"] {background:#ccc!important}
div[id="gmi-ResourcePageDisplayPane"] {background:none!important}

table[class="iconset-gruser f"] a, table[class="iconset-gruser f"] i[class="gtab-i gtabi-mc"] {background:none!important}
table[class="iconset-gruser f"] a[class="gtab gtabi-mc"], table[class="iconset-gruser f"] i[class="gtab-i gtabi-mc"] {border:none!important}

div[class="gruserbadge"] > h1 {color:#ff0040!important}

/*PROFILE.PAGE*/
[id="gmi-CComment"] [class="ch-ctrl"] [class="ctext ch"] [class="ch-ctrl cc-in"] {background:#e3e3e3!important}

[class="h cc-with-moods"] [class="ccname"], [class="h cc-with-moods"] [class="ccname"] a {color:#ff0042!important}

/*DEVIATION.PAGE*/
body #gmi-ResViewSizer_img.smshadow {border-color:#ddd!important}
#artist-comments .text-ctrl {border-color:#aaa!important}
span[style="float: right; margin-top: -13px; color: #597465; font-size: 11px"] {color:#000!important}
.details-section label, .details-section .label {color:#000!important;font-weight:600!important}
[style="padding:2px 12px 0 12px"] [class="f cc-copy"] {color:#ff0042!important}
[style="padding:2px 12px 0 12px"] [class="f cc-copy"] a {color:#ff0042!important}

/*CSS.GRADIENT.BUTTONS.AKA.DO.NOT.EDIT.BELOW.THIS.LINE*/
div[id="gmi-ResourcePageMetaPane"] .smbutton-green, div[id="gmi-ResourcePageMetaPane"] .smbutton {background:-moz-linear-gradient(-90deg, #ff6b74, #d8465c) repeat scroll 0 0 transparent!important}
div[id="gmi-ResourcePageMetaPane"] .smbutton, div[id="gmi-ResourcePageMetaPane"] #request-button strong, div[id="gmi-ResourcePageMetaPane"] #request-button small {color:#fff!important}
div[id="gmi-ResourcePageMetaPane"] [id="download-button"] strong, div[id="gmi-ResourcePageMetaPane"] [id="download-button"] small  {color:#fff!important}
div[id="gmi-ResourcePageMetaPane"] .smbutton strong, div[id="gmi-ResourcePageMetaPane"] .smbutton small {color:#fff!important}
/*CSS.GRADIENT.BUTTONS.AKA.DO.NOT.EDIT.ABOVE.THIS.LINE*/

span[style="color:#41573A"], span[style="color:#41573A"] small {color:#fff!important}
div[id="gmi-ResourcePageMetaPane"] .smbutton-green span, div[id="gmi-ResourcePageMetaPane"] .smbutton span {border-color:#ff0042!important}
[class="group_featured_list details-section"] [class="grptagline"] {color:#444!important}
[class="group_featured_list details-section"] [style="color: rgb(89, 116, 101); font-size: 9pt;"] {color:#444!important}

/*COLOR.TRYOUTS*/
.oh-l {color:#fff!important}
:link, *:visited {color:#111!important}
[class="smbutton smbutton-textonly"] strong {color:#ff0042!important}

/*SUBMIT.PAGE*/
div[id="submit"] {background:#e3e3e3!important}
div[id="submit"] [class="ch options-container"] {background:#ccc!important}
div[class="flatview c"] a[class="smbutton smbutton-green"] {background:-moz-linear-gradient(-90deg, #ff0042, #ff6655) repeat scroll 0 0 transparent!important;color:#fff!important;border-color:#ff0042!important}
div[class="flatview c"] .smbutton span {border-color:#ff6655!important}

div[class="ch options-container"] .groupselector_grp label[class="l_inline"] small {color:#444!important}

}

@-moz-document url("https://www.deviantart.com/settings/") {

[class="settings_wrapper"] {background:#bbb!important}
[class="menu_holder"] {background:#bbb!important}
[class^="header"] {color:#000!important}
[class="menu_holder"] a {background:#bbb!important;color:#000!important}
[class="menu_holder"] a:hover {background:#ccc!important}
a[class="brab active"] {background:#ff0042!important;color:#fff!important}
a:hover[class="brab active"] {background:#ff0042!important}
div[class="settings_form"] {background:#ccc!important}
div[class^="fooview"], [class^="altaltview"] {background:#e3e3e3!important}

div[id="output"], div[id="output"] div[class="bubbleview"] {background:#ccc!important}

}

@-moz-document domain("my.deviantart.com") {


[class="f messages-left"] {background:#bbb!important}
[class="f messages-left"] .ttext {background:#bbb!important;border-color:#aaa!important}
.page2 {background:#bbb!important}
.page2 [class^="icon"]{background:none!important}
.page2 .header {background:#aaa!important}
div[class="mczone"] {background:#ccc!important;border-color:#e3e3e3!important}
.messages-right i {display:none!important}
.mczone-title {background:#aaa!important;border-color:#aaa!important}

div.page2 a.f {background:#aaa!important}

/*OMG*/
.mcbox-preview-critique .ch-ctrl, .mcbox-preview-news .ch-ctrl, .mcbox-preview-journal .ch-ctrl, .mcbox-preview-forum .ch-ctrl, .mcbox-preview-stack .ch-ctrl, .mcbox-full-stack .ch-ctrl, .mcbox-thumb .ch-ctrl {background:#e3e3e3!important}

/*OMG2*/
body div.bubbleview div.mczone div.mcbox-full-comment div.ch-ctrl, body div.bubbleview div.mczone div.mcbox-full-helpnote div.ch-ctrl, body div.bubbleview div.mczone div.mcbox-full-poll div.ch-ctrl, body div.bubbleview div.mczone div.mcbox-fullnote div.ch-ctrl, body div.bubbleview div.mczone div.mcbox-full-comment i.c i, body div.bubbleview div.mczone div.mcbox-full-helpnote i.c i, body div.bubbleview div.mczone div.mcbox-fullnote i.c i, body div.bubbleview div.mczone div.mcbox-full-poll i.c i {background:#e3e3e3!important}
div.mcbox div.talkmessage {background:#e3e3e3!important}

div.mcb-tab .stacklink-cover .tabtext {background:#ccc!important;border-color:#ccc!important}

table[class="messages f full"] {background:#ccc!important}

[class="ch talkmessage-comment al"] {background:#ccc!important}
div.mcbox-full-comment div.mcb-whoicon {background:transparent!important}

}
