css:
@-moz-document url("https://www.wanikani.com/review/session") {
:root {
	--kpink: #b31d81;
	--kshadow:#851961;
	--rblue: #187aab;
	--rshadow: #125678;
	--vpurp: #7613a8;
	--vshadow: #560d7a;
	--graytext: #c9c9c9;
}
/*PURPLE background for VOCAB*/
#question #character.vocabulary{
	background-image: linear-gradient(to bottom, var(--vpurp), var(--vshadow));
	text-shadow: 5px 5px 0 var(--vshadow);
}
.highlight-vocabulary{
	background-color: var(--vpurp);
}
/*PINK background for KANJI*/
#question #character.kanji{
	background-image: linear-gradient(to bottom, var(--kpink), var(--kshadow));
	text-shadow: 5px 5px 0 var(--kshadow);
}
.highlight-kanji{
	background-color: var(--kpink);
}
/*BLUE background for RADICALS*/
#question #character.radical{
	background-image: linear-gradient(to bottom, var(--rblue), var(--rshadow));
	text-shadow: 5px 5px 0 var(--rshadow);
}
.highlight-radical{
	background-color: var(--rblue);
}
#question #character{
	color: var(--graytext);
}
#stats{
	color: var(--graytext);
}
h1{
	color: var(--graytext);
/*Answer form*/
}
#answer-form input[type=text]{
	box-shadow: 3px 3px 0 #000;
}
#answer-form fieldset.correct button input[type=text]:disabled{
    background-color: #77bb00 !important;
}
#user-response{
	color: var(--graytext);
	background-color: #666;
	border-bottom-color: #000;
}
#question #question-type.meaning{
	background-image: linear-gradient(to bottom, #666, #444);
	border-top: 1px solid #676767;
	border-bottom: 1px solid #000;
}
#answer-form button{
	background-color:#666;
}
.icon-chevron-right {
	color:var(--graytext);
}	
.srs .srs-down {
	background-color:#d4000e;
	color:#80001a;
}
.srs .srs-up {
	background-color:#72ab00;
	color:#4b7000;
}
/*Additional buttons*/
#additional-content ul li span{
	background-color:#444;
	color:var(--graytext);
	box-shadow: 3px 3px 0 #000;
}
#additional-content ul li:not(#option-audio) span:hover:before  {
	background-color:#777;
	color:#e3e3e3;
	box-shadow: 3px 3px 0 #000;
}
#answer-exception span::before{
	border-bottom-color: #8a8a8a;
}
/*Item info*/
#information {
	background-color:#555;
	color:var(--graytext);
	box-shadow: 3px 3px 0 #000;
}
#additional-content-load {
	background-color:#555;
}

#additional-content ul li.active::before {
	border-bottom-color: #555;
}
#answer-exception span{
	background-color: #8a8a8a;
	color: var(--graytext);
	box-shadow: 3px 3px 0 #000;
}
.audiopronunciations-component{
	color:#c9c9c9;
}
#item-info blockquote {
	background-color:#dedede;
	color:#777777;
}
h2 {
	color:var(--graytext);
}
#item-info #all-info{
	background-color:#dedede;
	color:#666;
}
body {
	background-color: #333;
}
}
