css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://twitter.com") {

body {background: #EEF2FF url("data:image/png;base64,") top center repeat-x !important;}


ol.statuses * {border:none !important;}

#content {
background: transparent!important; }
a:hover { color: #DD0000 !important;}

h2 {color : #117743 !important;}

.content-bubble-arrow {
opacity: 0!important;}

#side_base{
background-color: #B7C5D9 !important;
opacity:0.75 !important}

h2.sidebar-title{color : black !important;}


span.status-body a:hover { color: #DD0000 !important;
text-decoration: none!important;}
span.status-body .entry-content a {color: #DD0000 !important;}
span.status-body .screen-name {color: #117743 !important;}
a, a:visited { color: #34345C !important;}

/* Taken from http://userstyles.org/styles/16947 - thank you to the creator */
  .wrapper {
    padding-bottom: 8px !important;
  }

  #container {
    width: 90% !important;
  }

  #content {
    width: 1000px !important;
  }

  #onoff, .promotion, #device_updates, .yellow-box {
    display: none !important;
  }

  .info {
    padding: 0 10px !important;
  }

  #status-field-char-counter {
    margin-right: -5px !important;
  }

  #status {
    width: 100% !important;
    margin-top: 5px !important;
  }

  #currently {
    width: 65% !important;
  }

  .bulletin {
    display: none !important;
  }

  .section {
    padding-top: 0 !important;
    padding-bottom: 0 !important;
  }

  .status-body {
    width: 80% !important;
  }

  .stats {
    padding-bottom: 10px !important;
  }

/* Modified from Twitter Wheat theme - change hover background colour and shape */
ol.statuses li:hover {
background-color: #dee5f1 !important;
-moz-border-radius-topright: 10px !important;
-moz-border-radius-bottomleft: 10px !important;
}

/* Modified from twitter sidebar */

#timeline > li .thumb

 {padding-left : 5px !important;}

}
