css:
@-moz-document domain("communities.win"), domain("patriots.win"), domain("greatawakening.win") {
html, body, .main-content, .home .container, .search .container, .post-single .container, .profile .container, .page .container, .logs .container, .comment .details a[data-action=minimize], .header .bg {
	background-color: #323232;
}
.module, .sidebar blockquote, .popup .inner {
	background-color: #323232;
	background-image:none;
}
.post .vote .count {
	color: #ffffff;
}
.status-bar.locked {
	border: 1px solid #ff0000;
	background-color: #383838;
	color: #ffffff;
}
.status-bar.locked svg {
	background-color: #383838;
}
.header .bg .title {
	font-size: 30px;
}
.post-list .content {
	margin: 5px 0 0 115px!important;
}
.post-list .content .inner {
	background-color: #f0f8ff00;
	color: #fff;
}
.post-flair {
	color: #fff;
	border-color: #ad2b2b;
	background: #323232;
}
.header .bg .description {
	font-size:16px;
}
.filter-bar .listing-type select, .sidebar .filter-inner select, .filter-bar .filters select, .filter-bar .listing-type select, .sidebar .filter-inner select, .filter-bar .filters select {
	color:#fff;
	background-color: #323232;
}
.sidebar .timeframe {
	color: #ff0000;
}
.sidebar .filter-inner {
	color: #fff;
	background-color: #323232;
	border-left: 2px solid #404040;
	border-bottom: 2px solid #404040;
	border-right: 2px solid #404040;
	border-top: 2px solid #404040;
}
.sidebar .search input, .module .head, .sidebar h3, .sidebar .submit-link, .sidebar .submit-text {
	color: #fff;
	border-left: 2px solid #404040;
	border-bottom: 2px solid #404040;
	border-right: 2px solid #404040;
	border-top: 2px solid #404040;
}
.module, .popup .inner, .sidebar blockquote {
	background-color: #323232;
	border-left: 2px solid #404040;
	border-bottom: 2px solid #404040;
	border-right: 2px solid #404040;
}
.module .body, .sidebar blockquote {
	color: #4bbee2;
}
.module .head, .sidebar h3 {
	color: #ffffff;
	background-color: #292929;
}
.comment-parent .title {
	color: #42a9fb;
}
.comment-parent .author {
	color: #f3d01f;
}
.post-list .content {
	margin: 8px 0 0 174px;
}
.message-entry.unread {
	background-color: #474747;
	border: 1px solid #090909;
	padding: 0px;
}
.form.reply textarea {
	background-color: #323232;
	color: #fff;
}
.sidebar li {
	color: #fff;
}
.module.moderator-list.body.a {
	color:#0eff00;
}
.module .body p:last-child, .sidebar blockquote p:last-child, .module .body p, .sidebar blockquote p {
	color: #fff;
}
b, strong {
	color: #f00;
}
.module .body p, .sidebar blockquote p {
	margin-bottom: 4px;
}
.post.stickied {
	background: linear-gradient(#383838, rgba(255, 165, 0, 0.05));
	border-bottom: 2px solid rgba(255, 165, 0, .3);
	border-top: 2px solid rgba(255, 165, 0, .3);
	border-left: 2px solid rgba(255, 165, 0, .3);
	border-right: 2px solid rgba(255, 165, 0, .3);
	background-color: #323232;
}
.post {
	background: linear-gradient(#383838, #2F2F2F);
}
.post .title:visited {
	color: #c17dff;
}
.post .title {
	color: #67bcff;
	text-shadow: 2px 1px 4px #000;
	line-height: 14px;
}
.post-single .content p {
	text-shadow: 1px 1px 4px #000;
}
.nav-user .inner a:first-child {
	font-weight: 900;
	text-shadow: 2px 1px 4px #000;
}
.post.stickied .title, .nav-message a {
	color: #f00;
	background-color: rgba(255, 165, 0, 0);
}
.total, .post-single .content p, .footer, .footer a, .module .body p, .empty p, .main-content, .post .domain, .sidebar .mods .head p {
	text-shadow: 2px 1px 4px #000;
	color: #fff;
}
.post .domain {
	font-weight: 400;
}
.comment {
	border-top: 1px solid #484848;
	border-left: 2px solid #484848;
	background-color: #383838;
}
.content {
	color: #39dc25;
}
.module, .popup .inner {
	background-image: none;
	background-color: #323232;
	box-shadow: 0 0px 0px 0px grey;
}
.popup .subline, .popup .options label, .nav-profile .active, .nav-profile span, .nav-profile {
	color: #fff;
}
.popup .warning {
	font-size: 14px;
	color: #ff3c3c;
}
.actions[data-action=reply] {
	color: #3eafff;
}
.details a.author {
	color: #ff4949;
}
.sidebar .mods .body a, .sidebar .mods .head a, .sidebar .mods .foot a, .actions .comments, .mod-list a {
	color: #168be8;
	text-shadow: 2px 1px 4px #000;
}
.sidebar .mods {
	border: 1px solid;
	border-color: #23489c;
	background-color: #404040;
}
.sidebar .submit-link, .sidebar .submit-text {
	position: relative;
	display: block;
	margin: 0 0px 14px;
	background-color: #ae0000!important;
}
.module .body .heading {
	color: #168be8;
	text-shadow: 2px 1px 4px #000;
}
.nav-profile p, .nav-user .inner a:first-child {
	color: #f00;
}
.post .share {
	background-color: #323232;
	box-shadow: 0 0 2.75px 0.25px rgba(255, 255, 255, 0.2);
}
.form.edit input[type=submit], .form.edit input[data-action=cancel], .form.reply input[type=submit], .form.reply input[data-action=cancel] {
	background-color: #323232;
	border-color: #ff12126b;
	color: #fff;
}
.sidebar .search input {
	background-color: #323232!important;
	color: #fff!important;
}
.sidebar .login input[type=text], .sidebar .login input[type=password], .single .form input[type=text], .single .form input[type=email], .single .form input[type=password], .single .form input[type=url], .single .form textarea, .page .main-content .form input[type=text], .page .main-content .form input[type=email], .page .main-content .form input[type=password], .page .main-content .form input[type=number], .page .main-content .form textarea, .popup .form input[type=text], .popup .form input[type=number], .popup .form textarea {
	background-color: #323232!important;
	color: #fff!important;
}
.single .form select {
	background-color: #323232;
	color: #fff!important;
}
.post-single .sort select {
	background-color: #323232;
	color: #fff;
	border: 1px solid;
	border-color: #f00;
}
.single .tags input[type=checkbox]+label {
	color: #fff;
	background-color: #484848;
}
.single .tags input[type=checkbox]:checked+label {
	color: #ffffff;
	background-color: #168be8;
	border: 1px solid #000000;
}
.more a:hover {
	background-color: #444;
	border: 1px solid #ff1d42;
	color: #f5f5fd;
}
.more a {
	background-color: #333;
	border: 1px solid #ff1d42;
}
.content {
	color: #fff;
}
.content a {
	color: #168be8;
	text-decoration: underline;
}
em, i {
	color: #25d442;
}
.nav-user .inner .unread svg {
	color: #00ff0a;
}
.post .vote a[data-direction=up] {
	font-size: 18px;
	margin-bottom: 8px;
}
.post .vote a[data-direction=down] {
	font-size: 18px;
	margin-top: 8px;
}
.comment .vote a[data-direction=up] {
	margin-top: 8px;
	margin-bottom: 14px;
}
a:hover {
	color: #ff0;
}
.header .bar, .nav-user, .module .head, .popup .head {
	background-image: none;
	background-color: #252525;
}
.module .body {
	border-color: #b30000;
}
.module .body p {
	width: 100%
}
.home .footer {
	display:none;
}
}
