css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("linuxsir.org") {

body {
    background: #444444 !important;
    color: #cccccc !important;
}

a {
    color: lightblue !important;
}

/* wrapper */
.page {
    background: #444444 !important;
    color: #cccccc !important;
}

.time {
    color: #cccccc !important;
}

/* forum table */
.alt1, .alt1Active {
    background: #333333 !important;
    color: #cccccc !important;
}
.alt2, .alt2Active {
    background: #444444 !important;
    color: #cccccc !important;
}

/* post list */
/* .tborder { */
    /* background: #333333 !important; */
/* } */
.thead {
    background: #336699 !important;
}

/* user detail in post left */
.smallfont {
    color: #cccccc !important;
}

/* reply */
.panelsurround, .panel {
    background: #333333 !important;
    color: #cccccc !important;
}
.vBulletin_editor {
    background: #333333 !important;
}
legend {
    color: #cccccc !important;
}


}
