css:
@-moz-document regexp('(http|https)://moodle.*'), domain("moodle.itech-bs14.de") {
  body {
      background-color: #1b212c;
      color: #ffffff;
  }
  .bg-white {
      background-color: #2d364a !important;
  }
  .bg-dark {
      background-color: #2d364a!important;
  }
  .bg-light {
      background-color: #2d364a!important;
  }
  #region-main {
      background-color: #2d364a;
  }
  div#page-content {
      background-color: #1b212c;
  }
  div#page-header {
      background-color: #1b212c;
  }
  .dropdown-menu {
      background-color: #2d364a;
      border: 1px solid rgb(155 186 255);
  }
  .dropdown-item {
      color: #ffffff;
  }
  .jumbotron {
      background-color: #2d364a;
  }
  .btn-outline-dark {
      color: #d8d8d8;
      border-color: #9bbaff;
  }
  .btn-dark {
      color: #fff!important;
      background-color: #1b212c!important;
      border-color: #9bbaff!important;
  }
  .btn-primary {
      color: #fff!important;
      background-color: #1b212c!important;
      border-color: #9bbaff!important;
  }
  .list-group-item {
      background-color: #2d364a;
  }
  .list-group-item-action {
      color: #ffffff;
  }
  .list-group-item.active {
      background-color: #1b212c;
      border-color: #2d364a;
  }
  .list-group-item-action:hover, .list-group-item-action:focus {
      color: #ffffff;
      background-color: #4b5c84;
  }
  [data-region="drawer"] {
      background-color: #1b212c;
  }
  .btn-link {
      color: #90b7ff;
  }
  .btn-light {
      color: #9bbaff;
      background-color: #f8f9fa;
      border-color: #f8f9fa6b;
      border-radius: 3px;
  }
  .btn-primary {
      color: #fff;
      background-color: #1b212c;
      border-color: #828894;
  }
  .custom-select {
      color: #9bbaff;
      background-color: #2d364a;
      border-color: #f8f9fa6b;
      border-radius: 3px;
  }
  .form-control {
      color: #ffffff;
      background-color: #1b212c;
      border-radius: 3px;
      border-color: #828894;
  }
  .form-control:focus {
      color: #ffffff;
      background-color: #1b212c;
      border-color: #828894;
      outline: 0;
      -webkit-box-shadow: 0 0 0 0.2rem rgba(69,69,69,.75);
      box-shadow: 0 0 0 0.2rem rgba(69,69,69,.75);
  }
  .modal-content {
      background-color: #323c53;
  }
  .calendar_event_course {
      background-color: #0e1116;
  }
  header .card {
      background-image: unset;
      background-color: #ffffff00;
  }
  .card {
      background-color: #2d364a;
  }
  .card-header {
      background-color: rgb(27 33 44);
  }
  .bg-gray {
      background-color: #2d364a!important;
  }
  .navbar-light .navbar-nav .nav-link {
      color: rgb(255, 255, 255, 0.6);
  }
  .navbar-light .navbar-brand {
      color: rgb(255, 255, 255, 0.9);
  }
  .navbar-light .navbar-nav .nav-link:hover, .navbar-light .navbar-nav .nav-link:focus {
      color: rgb(255 255 255);
  }
  a {
      color: #ffffff;
  }
  ::-webkit-scrollbar {
    width: 6px;
    height: 6px;
  }
  ::-webkit-scrollbar-button {
    width: 0px;
    height: 0px;
  }
  ::-webkit-scrollbar-thumb {
    background: #525d77;
    border: 0px none #ffffff;
    border-radius: 50px;
  }
  ::-webkit-scrollbar-thumb:hover {
    background: #acc3f7;
  }
  ::-webkit-scrollbar-thumb:active {
    background: #9bbaff;
  }
  ::-webkit-scrollbar-track {
    background: #2d364a;
    border: 0px none #ffffff;
    border-radius: 50px;
  }
  ::-webkit-scrollbar-track:hover {
    background: #2d364a;
  }
  ::-webkit-scrollbar-track:active {
    background: #424d65;
  }
  ::-webkit-scrollbar-corner {
    background: transparent;
  }
}
