css:
@-moz-document url-prefix('https://www.gmx.fr/') {
    * {
        visibility: hidden !important;
        overflow: hidden;
    }
    .login-layer, .login-layer *, .layer-box, .layer-box *, .welcome-box, .welcome-box *, .error, .error *, .blurredbg, .permission-layer-default, .permission-layer-default * {
        visibility: visible !important;
    }
    .login-layer {
        position: fixed;
        height: 500px;
        top: 25% !important;
        left: 0;
    }
    .mobile-signup *, .close, .btn-share, .btn-share * {
      display : none;
      visibility: hidden !important;
    }
}
@-moz-document url-prefix('https://www.gmx.fr/logout') {
    * {
        visibility: hidden !important;
    }
    .msg, .msg *, .button-login, .button-login * {
        visibility: visible !important;
    }
    .mobile-signup *, .close, .btn-share, .btn-share * {
        display : none;
        visibility: hidden !important;
    }
}
